<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Thermodynamic Visualization</title>
    <style>
        #main-container {
            max-width: 800px;
            margin: 20px auto;
        }

        #controls-container {
            background-color: #f0f0f0;
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 5px;
        }

        #control-group-pressure, #control-group-temperature {
            display: none;
        }

        #control-group-pressure.show, #control-group-temperature.show {
            display: block;
        }

        #checkbox-container {
            display: none;
        }

        #checkbox-container.show {
            display: block;
        }

        #plot-container {
            height: 400px;
            width: 100%;
        }

        .plotly-graph-div {
            height: 400px;
            width: 100%;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" value="0.08" min="0.01" max="0.20" step="0.005">
                <span id="value-pressure"></span>
            </div>
            <div id="control-group-temperature" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" value="475" min="450" max="490" step="1">
                <span id="value-temperature"></span>
            </div>
            <select id="select-plot">
                <option value="vs_temp">fugacity versus temperature</option>
                <option value="vs_press">fugacity versus pressure</option>
            </select>
            <div id="checkbox-container" style="display: none;">
                <input type="checkbox" id="checkbox-high-pressure" checked>
                <label for="checkbox-high-pressure">high pressure</label>
            </div>
        </div>
        <div id="plot-container" class="plotly-graph-div"></div>
    </div>
    <script>
        const plotContainer = document.getElementById('plot-container');
        const plotDiv = document.createElement('div');
        plotDiv.id = 'plot';
        plotContainer.appendChild(plotDiv);

        const selectPlot = document.getElementById('select-plot');
        const sliderPressure = document.getElementById('slider-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');

        let plotType = 'vs_temp';
        let pressure = 0.08;
        let temperature = 475;
        let highPressure = true;

        selectPlot.addEventListener('change', () => {
            plotType = selectPlot.value;
            updatePlot();
        });

        sliderPressure.addEventListener('input', () => {
            pressure = parseFloat(sliderPressure.value);
            document.getElementById('value-pressure').innerText = `pressure: ${pressure.toFixed(2)} bar`;
            updatePlot();
        });

        sliderTemperature.addEventListener('input', () => {
            temperature = parseFloat(sliderTemperature.value);
            document.getElementById('value-temperature').innerText = `temperature: ${temperature} K`;
            updatePlot();
        });

        checkboxHighPressure.addEventListener('change', () => {
            highPressure = checkboxHighPressure.checked;
            updatePlot();
        });

        function updatePlot() {
            const plot = plotDiv;
            plot.innerHTML = '';
            plot.style.height = '400px';
            plot.style.width = '100%';

            if (plotType === 'vs_temp') {
                const x = [];
                const y = [];
                const saturationPressure = 0.08;
                const temperatureRange = [280, 400];

                for (let t = temperatureRange[0]; t <= temperatureRange[1]; t += 1) {
                    const fSat = Math.pow(10, (8.65 - 5818 / (230 + t)));
                    x.push(t);
                    y.push(fSat);

                    if (t >= 5818 / (8.65 - Math.log10(saturationPressure)) - 230) {
                        break;
                    }
                }

                const stableLiquid = {
                    x: x,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#0000FF',
                        width: 2
                    }
                };

                const stableVapor = {
                    x: [5818 / (8.65 - Math.log10(saturationPressure)) - 230, temperatureRange[1]],
                    y: [saturationPressure, saturationPressure],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#0000FF',
                        width: 2
                    }
                };

                const metastableLiquid = {
                    x: [5818 / (8.65 - Math.log10(saturationPressure)) - 230, temperatureRange[1]],
                    y: [Math.pow(10, (8.65 - 5818 / (230 + (5818 / (8.65 - Math.log10(saturationPressure)) - 230)))], temperatureRange[1]],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#A0A0FF',
                        width: 2,
                        dash: 'dash'
                    }
                };

                const metastableVapor = {
                    x: x,
                    y: [saturationPressure, saturationPressure],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#A0A0FF',
                        width: 2,
                        dash: 'dash'
                    }
                };

                const saturationPoint = {
                    x: [5818 / (8.65 - Math.log10(saturationPressure)) - 230],
                    y: [saturationPressure],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: '#000000',
                        size: 8
                    }
                };

                const indicatorLine = {
                    x: [5818 / (8.65 - Math.log10(saturationPressure)) - 230],
                    y: [0, saturationPressure],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#000000',
                        width: 2,
                        dash: 'dash'
                    }
                };

                Plotly.newPlot(plot, [stableLiquid, stableVapor, metastableLiquid, metastableVapor, saturationPoint, indicatorLine], {
                    paper_bgcolor: 'white',
                    plot_bgcolor: 'white',
                    gridcolor: '#e0e0e0',
                    font: {
                        family: 'Arial',
                        size: 14
                    },
                    xaxis: {
                        title: 'temperature (°C)',
                        range: [280, 400]
                    },
                    yaxis: {
                        title: 'fugacity (bar)',
