    <script>
        // Initialize plot
        let plotDiv = document.getElementById('plot-container');
        let plot = Plotly.newPlot(plotDiv, [], {
            xaxis: { title: 'x', range: [0, 1], tickmode: 'linear', dtick: 0.2 },
            yaxis: { title: 'T', range: [-0.1, 1.1], tickmode: 'linear', dtick: 0.2 },
            showlegend: false,
            grid: { pattern: 'lines', color: '#ccc' },
            margin: { t: 20, r: 20, b: 40, l: 40 }
        });

        // Update plot when slider values change
        document.getElementById('slider-points').addEventListener('input', (e) => {
            let numPoints = parseInt(e.target.value);
            let N = numPoints - 1;
            let x = [];
            let y = [];
            for (let j = 0; j <= N; j++) {
                let x_j = (Math.cos(j * Math.PI / N) + 1) / 2;
                let T_j = (8 / Math.PI ** 2) * Array.from({ length: 100 }, (_, i) => (1 / (i + 1) ** 2) * Math.sin((i + 1) * Math.PI / 2) * Math.sin((i + 1) * Math.PI * x_j) * Math.exp(-2 * (i + 1) ** 2 * Math.PI ** 2 * 0.05));
                x.push(x_j);
                y.push(T_j[99]); // Use the last calculated value for the plot
            }
            plot.data = [
                {
                    x: x,
                    y: y,
                    mode: 'markers',
                    marker: { color: 'red', size: 8 }
                },
                {
                    x: x,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 3 }
                }
            ];
            plot.layout.xaxis.range = [0, 1];
            plot.layout.yaxis.range = [-0.1, 1.1];
            plot.update();
            document.getElementById('label-points').textContent = numPoints;
        });

        document.getElementById('slider-time').addEventListener('input', (e) => {
            let t = parseFloat(e.target.value);
            let x = [];
            let y = [];
            for (let j = 0; j < 200; j++) {
                let x_j = j / 199;
                let T_j = (8 / Math.PI ** 2) * Array.from({ length: 100 }, (_, i) => (1 / (i + 1) ** 2) * Math.sin((i + 1) * Math.PI / 2) * Math.sin((i + 1) * Math.PI * x_j) * Math.exp(-2 * (i + 1) ** 2 * Math.PI ** 2 * t));
                x.push(x_j);
                y.push(T_j[99]); // Use the last calculated value for the plot
            }
            plot.data[1].x = x;
            plot.data[1].y = y;
            plot.data[0].x = x;
            plot.data[0].y = y;
            plot.layout.xaxis.range = [0, 1];
            plot.layout.yaxis.range = [-0.1, 1.1];
            plot.update();
            document.getElementById('label-time').textContent = t.toFixed(3);
        });
    </script>
</body>
