                textSize(12);
                textAlign(CENTER, CENTER);
                text('S', steinerPoints[i].x, steinerPoints[i].y);
            }
            for (let i = 0; i < edges.length; i++) {
                stroke(190, 160, 130, 0.5);
                line(edges[i].x1, edges[i].y1, edges[i].x2, edges[i].y2);
            }
            for (let i = 0; i < edges.length; i++) {
                if (edges[i].mst) {
                    stroke(0);
                    line(edges[i].x1, edges[i].y1, edges[i].x2, edges[i].y2);
                }
            }
            if (showAngles) {
                for (let i = 0; i < steinerPoints.length; i++) {
                    let neighbors = [];
                    for (let j = 0; j < edges.length; j++) {
                        if (edges[j].x1 === steinerPoints[i].x && edges[j].y1 === steinerPoints[i].y) {
                            neighbors.push(edges[j].x2 - steinerPoints[i].x);
                            neighbors.push(edges[j].y2 - steinerPoints[i].y);
                        } else if (edges[j].x2 === steinerPoints[i].x && edges[j].y2 === steinerPoints[i].y) {
                            neighbors.push(edges[j].x1 - steinerPoints[i].x);
                            neighbors.push(edges[j].y1 - steinerPoints[i].y);
                        }
                    }
                    neighbors.sort((a, b) => a - b);
                    for (let j = 0; j < neighbors.length - 1; j++) {
                        let angle = Math.atan2(neighbors[j + 1], neighbors[j]) * 180 / Math.PI;
                        let textX = steinerPoints[i].x + Math.cos(angle * Math.PI / 180) * 20;
                        let textY = steinerPoints[i].y + Math.sin(angle * Math.PI / 180) * 20;
                        fill(0);
                        textSize(12);
                        textAlign(CENTER, CENTER);
                        text(Math.round(angle), textX, textY);
                    }
                }
            }
            fill(0);
            textSize(24);
            textAlign(CENTER, CENTER);
            text(totalLength, width / 2, height - 20);
        }

        function calculatePoints() {
            let angle = 0;
            for (let i = 0; i < numRegularPoints; i++) {
                points.push({ x: width / 2 + Math.cos(angle) * 200, y: height / 2 + Math.sin(angle) * 200 });
                angle += 2 * Math.PI / numRegularPoints;
            }
            for (let i = 0; i < numSteinerPoints; i++) {
                steinerPoints.push({ x: width / 2, y: height / 2 });
            }
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    edges.push({ x1: points[i].x, y1: points[i].y, x2: points[j].x, y2: points[j].y, mst: false });
                }
            }
            for (let i = 0; i < steinerPoints.length; i++) {
                for (let j = 0; j < points.length; j++) {
                    edges.push({ x1: steinerPoints[i].x, y1: steinerPoints[i].y, x2: points[j].x, y2: points[j].y, mst: false });
                }
            }
            for (let i = 0; i < steinerPoints.length; i++) {
                for (let j = i + 1; j < steinerPoints.length; j++) {
                    edges.push({ x1: steinerPoints[i].x, y1: steinerPoints[i].y, x2: steinerPoints[j].x, y2: steinerPoints[j].y, mst: false });
                }
            }
            edges.sort((a, b) => dist(a.x1, a.y1, a.x2, a.y2) - dist(b.x1, b.y1, b.x2, b.y2));
            let mst = [];
            let total = 0;
            for (let i = 0; i < edges.length; i++) {
                if (mst.length === 0 || !contains(mst, edges[i])) {
                    mst.push(edges[i]);
                    total += dist(edges[i].x1, edges[i].y1, edges[i].x2, edges[i].y2);
                }
            }
            totalLength = total;
            for (let i = 0; i < mst.length; i++) {
                mst[i].mst = true;
            }
            edges = mst;
        }

        function contains(arr, obj) {
            for (let i = 0; i < arr.length; i++) {
                if (arr[i].x1 === obj.x1 && arr[i].y1 === obj.y1 && arr[i].x2 === obj.x2 && arr[i].y2 === obj.y2) {
                    return true;
                }
            }
            return false;
        }

        function draw() {
            drawNetwork();
        }

        function mousePressed() {
            for (let i = 0; i < points.length; i++) {
                let d = dist(mouseX, mouseY, points[i].x, points[i].y);
                if (d < 10) {
                    points[i].x = mouseX;
                    points[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
            for (let i = 0; i < steinerPoints.length; i++) {
                let d = dist(mouseX, mouseY, steinerPoints[i].x, steinerPoints[i].y);
                if (d < 10) {
                    steinerPoints[i].x = mouseX;
                    steinerPoints[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
        }

        function mouseDragged() {
            for (let i = 0; i < points.length; i++) {
                let d = dist(mouseX, mouseY, points[i].x, points[i].y);
                if (d < 10) {
                    points[i].x = mouseX;
                    points[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
            for (let i = 0; i < steinerPoints.length; i++) {
                let d = dist(mouseX, mouseY, steinerPoints[i].x, steinerPoints[i].y);
                if (d < 10) {
                    steinerPoints[i].x = mouseX;
                    steinerPoints[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
        }

        function mouseReleased() {
            for (let i = 0; i < points.length; i++) {
                let d = dist(mouseX, mouseY, points[i].x, points[i].y);
                if (d < 10) {
                    points[i].x = mouseX;
                    points[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
            for (let i = 0; i < steinerPoints.length; i++) {
                let d = dist(mouseX, mouseY, steinerPoints[i].x, steinerPoints[i].y);
                if (d < 10) {
                    steinerPoints[i].x = mouseX;
                    steinerPoints[i].y = mouseY;
                    calculatePoints();
                    drawNetwork();
                }
            }
        }

        function checkboxChanged() {
            showAngles = document.getElementById('checkbox-show-angles').checked;
            drawNetwork();
        }

        document.getElementById('checkbox-show-angles').addEventListener('change', checkboxChanged);

        document.getElementById('btn-regular-3').addEventListener('click', function () {
            numRegularPoints = 3;
            document.getElementById('btn-regular-3').classList.add('active');
            document.getElementById('btn-regular-4').classList.remove('active');
            document.getElementById('btn-regular-5').classList.remove('active');
            calculatePoints();
            drawNetwork();
        });

        document.getElementById('btn-regular-4').addEventListener('click', function () {
            numRegularPoints = 4;
            document.getElementById('btn-regular-3').classList.remove('active');
            document.getElementById('btn-regular-4').classList.add('active');
            document.getElementById('btn-regular-5').classList.remove('active');
            calculatePoints();
            drawNetwork();
        });

        document.getElementById('btn-regular-5').addEventListener('click', function () {
            numRegularPoints = 5;
            document.getElementById('btn-regular-3').classList.remove('active');
            document.getElementById('btn-regular-4').classList.remove('active');
            document.getElementById('btn-regular-5').classList.add('active');
            calculatePoints();
            drawNetwork();
        });

        document.getElementById('btn-steiner-0').addEventListener('click', function () {
            numSteinerPoints = 0;
            document.getElementById('btn-steiner-0').classList.add('active');
            document.getElementById('btn-steiner-1').classList.remove('active');
            document.getElementById('btn-steiner-2').classList.remove('active');
            document.getElementById('btn-steiner-3').classList.remove('active');
            calculatePoints();
            drawNetwork();
