    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/dist/p5.js"></script>
    <script>
        let means = [0, 0, 0, 0];
        let standardDeviations = [1, 1, 1, 1];
        let grandMean;
        let msBetween;
        let msWithin;
        let fRatio;

        function setup() {
            createCanvas(800, 600);
            background(220);
            noLoop();
        }

        function draw() {
            background(220);
            drawGroupDataPlot();
            drawAnovaResultsPlot();
        }

        function drawGroupDataPlot() {
            // Draw axes and title
            stroke(0);
            line(100, 50, 100, height - 50);
            line(100, 50, 200, 50);
            fill(0);
            text("W", 90, 30);
            text("X", 190, 30);
            text("Y", 290, 30);
            text("Z", 390, 30);
            text("F ratio = " + nf(fRatio, 2, 2), 10, 30);

            // Draw grand mean line
            stroke(0);
            line(100, 50, 100, height - 50);
            strokeWeight(2);
            line(100, 50, 100, height - 50);
            noStroke();
            fill(0);
            text(nf(grandMean, 2, 2), 120, 30);

            // Draw group bars
            for (let i = 0; i < means.length; i++) {
                fill(orange);
                rect(100 + i * 100, height - 50 - means[i] - standardDeviations[i], 90, means[i] + standardDeviations[i]);
                stroke(0);
                line(100 + i * 100 + 45, height - 50 - means[i], 100 + i * 100 + 45, height - 50 - means[i] - standardDeviations[i]);
            }
        }

        function drawAnovaResultsPlot() {
            // Draw title and axes
            fill(0);
            text("Analysis of the F-Ratio", 10, 20);
            stroke(0);
            line(0, 50, width, 50);
            line(0, 50, 0, height - 50);
            fill(0);
            text("MS Between", 10, 70);
            text("MS Within", 10, 110);
            text("F-Ratio", 10, 150);

            // Draw bars
            for (let i = 0; i < 3; i++) {
                let x = 0;
                if (i == 0) {
                    fill(teal);
                    x = map(msBetween, 0, 200, 0, width);
                } else if (i == 1) {
                    fill(gold);
                    x = map(msWithin, 0, 200, 0, width);
                } else if (i == 2) {
                    fill(purple);
                    x = map(fRatio, 0, 200, 0, width);
                }
                rect(x, 50, width - x, 50);
                fill(0);
                text(["MS Between", "MS Within", "F-Ratio"][i], 10, 70 + i * 40);
            }
        }

        function update() {
            means = [parseFloat(document.getElementById("slider-mean-w").value), parseFloat(document.getElementById("slider-mean-x").value), parseFloat(document.getElementById("slider-mean-y").value), parseFloat(document.getElementById("slider-mean-z").value)];
            standardDeviations = [parseFloat(document.getElementById("slider-sd-w").value), parseFloat(document.getElementById("slider-sd-x").value), parseFloat(document.getElementById("slider-sd-y").value), parseFloat(document.getElementById("slider-sd-z").value)];
            grandMean = means.reduce((a, b) => a + b, 0) / means.length;
            msBetween = means.reduce((a, b, i) => a + (b - grandMean) ** 2, 0) * 10;
            msWithin = standardDeviations.reduce((a, b, i) => a + b ** 2 * 9, 0);
            fRatio = msBetween / msWithin;
            document.getElementById("value-mean-w").textContent = nf(means[0], 1, 1);
            document.getElementById("value-mean-x").textContent = nf(means[1], 1, 1);
            document.getElementById("value-mean-y").textContent = nf(means[2], 1, 1);
            document.getElementById("value-mean-z").textContent = nf(means[3], 1, 1);
            document.getElementById("value-sd-w").textContent = nf(standardDeviations[0], 1, 1);
            document.getElementById("value-sd-x").textContent = nf(standardDeviations[1], 1, 1);
            document.getElementById("value-sd-y").textContent = nf(standardDeviations[2], 1, 1);
            document.getElementById("value-sd-z").textContent = nf(standardDeviations[3], 1, 1);
            redraw();
        }

        function inputEvent() {
            update();
        }

        for (let i = 0; i < 8; i++) {
            document.getElementById(`slider-mean-${["w", "x", "y", "z"][Math.floor(i / 2)]}`).addEventListener("input", inputEvent);
            document.getElementById(`slider-sd-${["w", "x", "y", "z"][Math.floor(i / 2)]}`).addEventListener("input", inputEvent);
        }

        setup();
        update();
    </script>
</body>
</html>
