    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        let mu1 = 3.87;
        let sigma1 = 0.198;
        let xi1 = -0.05;
        let mu2 = 3.87;
        let sigma2 = 0.3;
        let xi2 = -0.05;
        let pmax = 100;

        function update() {
            // Update read-only text inputs
            document.getElementById('display-mu1').value = mu1.toFixed(2);
            document.getElementById('display-sigma1').value = sigma1.toFixed(2);
            document.getElementById('display-xi1').value = xi1.toFixed(2);
            document.getElementById('display-mu2').value = mu2.toFixed(2);
            document.getElementById('display-sigma2').value = sigma2.toFixed(2);
            document.getElementById('display-xi2').value = xi2.toFixed(2);
            document.getElementById('display-pmax').value = pmax;

            // Update PDF plot
            let x = Array.from({length: 100}, (_, i) => i / 100 + 2.5);
            let y1 = [];
            let y2 = [];
            for (let i = 0; i < x.length; i++) {
                let t = 1 + xi1 * ((x[i] - mu1) / sigma1);
                let f = (1 / sigma1) * Math.pow(t, xi1 + 1) * Math.exp(-t);
                y1.push(f);
                t = 1 + xi2 * ((x[i] - mu2) / sigma2);
                f = (1 / sigma2) * Math.pow(t, xi2 + 1) * Math.exp(-t);
                y2.push(f);
            }
            let data = [
                {
                    x: x,
                    y: y1,
                    name: 'GEV 1',
                    line: {color: 'blue', shape: 'spline'},
                    fill: 'tozeroy',
                    fillcolor: 'rgba(0, 0, 255, 0.2)'
                },
                {
                    x: x,
                    y: y2,
                    name: 'GEV 2',
                    line: {color: 'red', shape: 'spline'},
                    fill: 'tozeroy',
                    fillcolor: 'rgba(255, 0, 0, 0.2)'
                }
            ];
            Plotly.newPlot('plot-pdf', data, {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: {range: [2.5, 7]},
                yaxis: {title: ''}
            });

            // Update return level plot
            let y1rl = [];
            let y2rl = [];
            for (let i = 0; i <= pmax; i++) {
                let y1 = mu1 - (sigma1 / xi1) * (1 - (-Math.log(1 - 1 / (i + 1))) ** (-xi1));
                y1rl.push(y1);
                let y2 = mu2 - (sigma2 / xi2) * (1 - (-Math.log(1 - 1 / (i + 1))) ** (-xi2));
                y2rl.push(y2);
            }
            data = [
                {
                    x: Array.from({length: pmax + 1}, (_, i) => i + 1),
                    y: y1rl,
                    name: 'GEV 1',
                    line: {color: 'blue'}
                },
                {
                    x: Array.from({length: pmax + 1}, (_, i) => i + 1),
                    y: y2rl,
                    name: 'GEV 2',
                    line: {color: 'red'}
                }
            ];
            Plotly.newPlot('plot-return-level', data, {
                title: '',
                xaxis: {title: 'Return Period (years)', range: [0, pmax + 1]},
                yaxis: {title: 'Maximum Sea Level (meters)'}
            });
        }

        // Update function for all sliders
        document.getElementById('slider-mu1').addEventListener('input', update);
        document.getElementById('slider-sigma1').addEventListener('input', update);
        document.getElementById('slider-xi1').addEventListener('input', update);
        document.getElementById('slider-mu2').addEventListener('input', update);
        document.getElementById('slider-sigma2').addEventListener('input', update);
        document.getElementById('slider-xi2').addEventListener('input', update);
        document.getElementById('slider-pmax').addEventListener('input', update);

        // Initial update
        update();
    </script>
</body>
</html>
