    <script src="https://cdn.mathjax.org/mathjax/latest/MathJax.js?config=TeX-AMS-MML_HTMLorMML"></script>
    <script>
        // Signal definitions
        const signals = {
            Triangle: [[0, 0], [3, 4], [6, 0]],
            RampDown: [[0, 4], [6, 0]],
            TopHat: [[1, 0], [1, 4], [5, 4], [5, 0]],
            StairStep: [[1, 0], [1, 1], [2, 1], [2, 4], [4, 4], [4, 1], [5, 1], [5, 0]],
            UH: [[1, 0], [1, 4], [1.5, 4], [1.5, 1], [2.5, 1], [2.5, 4], [3, 4], [3, 0], [4, 0], [4, 4], [4.5, 4], [4.5, 2], [5.5, 2], [5.5, 4], [6, 4], [6, 0]]
