                        ax: 0,
                        ay: -10,
                        xanchor: 'left',
                        yanchor: 'bottom',
                        axref: 'x',
                        ayref: 'y',
                        font: { size: 18 }
                    }]
                }
            };

            // Update bottom plot
            const bottomPlot = {
                data: [{
                    x: x.map(([t, v]) => t),
                    y: x.map(([t, v]) => v),
                    mode: 'lines',
                    line: { color: 'steelblue', width: 2.5 },
