                        text: 'T',
                        xref: 'x',
                        yref: 'y',
                        showarrow: false,
                        ax: 0,
                        ay: -10,
                        xanchor: 'left',
                        yanchor: 'bottom',
                        axref: 'x',
                        ayref: 'y',
