                        ax: 0,
                        ay: -10,
                        xanchor: 'left',
                        yanchor: 'bottom',
                        axref: 'x',
                        ayref: 'y',
                        font: { size: 18 }
                    }]
                }
            };

            // Update plot visibility
            if (!checkX) {
                bottomPlot.data[0].visible = false;
            } else {
                bottomPlot.data[0].visible = true;
            }

            if (!checkY) {
                bottomPlot.data[1].visible = false;
            } else {
                bottomPlot.data[1].visible = true;
            }

            if (!checkZ) {
                bottomPlot.data[2].visible = false;
            } else {
                bottomPlot.data[2].visible = true;
            }

            if (!checkW) {
                bottomPlot.data[3].visible = false;
            } else {
                bottomPlot.data[3].visible = true;
            }

            // Update plots
            Plotly.newPlot('plot-top', topPlot.data, topPlot.layout);
            Plotly.newPlot('plot-bottom', bottomPlot.data, bottomPlot.layout);
        }

        // Event listeners
        document.getElementById('btn-triangle').addEventListener('click', () => {
            activeSignal = 'Triangle';
            updatePlots();
        });

        document.getElementById('btn-rampdown').addEventListener('click', () => {
            activeSignal = 'RampDown';
            updatePlots();
        });

        document.getElementById('btn-tophat').addEventListener('click', () => {
            activeSignal = 'TopHat';
            updatePlots();
        });

        document.getElementById('btn-stairstep').addEventListener('click', () => {
            activeSignal = 'StairStep';
