            updatePlots();
        });

        document.getElementById('slider-b').addEventListener('input', () => {
            b = parseFloat(document.getElementById('slider-b').value);
            T = b / a;
            document.getElementById('slider-b-value').textContent = b.toFixed(1);
            document.getElementById('slider-t').value = T.toFixed(1);
            updatePlots();
        });

        document.getElementById('slider-t').addEventListener('input', () => {
            T = parseFloat(document.getElementById('slider-t').value);
            b = a * T;
            document.getElementById('slider-t-value').textContent = T.toFixed(1);
            document.getElementById('slider-b').value = b.toFixed(1);
            updatePlots();
        });

        document.getElementById('check-x').addEventListener('change', () => {
            checkX = document.getElementById('check-x').checked;
            updatePlots();
        });

        document.getElementById('check-y').addEventListener('change', () => {
            checkY = document.getElementById('check-y').checked;
            updatePlots();
        });

        document.getElementById('check-z').addEventListener('change', () => {
            checkZ = document.getElementById('check-z').checked;
            updatePlots();
        });

        document.getElementById('check-w').addEventListener('change', () => {
            checkW = document.getElementById('check-w').checked;
            updatePlots();
        });

        // Initial update
        updatePlots();
    </script>
</body>
</html>
