</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label>units in x direction</label>
                <input type="radio" name="unitsX" id="radio-x-0" value="0"> 0
                <input type="radio" name="unitsX" id="radio-x-1" value="1"> 1
                <input type="radio" name="unitsX" id="radio-x-2" value="2" checked> 2
                <input type="radio" name="unitsX" id="radio-x-3" value="3"> 3
                <input type="radio" name="unitsX" id="radio-x-4" value="4"> 4
            </div>
            <div>
                <label>units in y direction</label>
                <input type="radio" name="unitsY" id="radio-y-0" value="0"> 0
                <input type="radio" name="unitsY" id="radio-y-1" value="1"> 1
                <input type="radio" name="unitsY" id="radio-y-2" value="2" checked> 2
                <input type="radio" name="unitsY" id="radio-y-3" value="3"> 3
                <input type="radio" name="unitsY" id="radio-y-4" value="4"> 4
            </div>
            <div>
                <label>trim</label>
                <input type="range" id="slider-trim" value="0.8" min="0.65" max="0.9" step="0.001">
                <span id="label-trim-value">0.8</span>
            </div>
            <div>
                <input type="checkbox" id="checkbox-double"> <label for="checkbox-double">double</label>
                <input type="checkbox" id="checkbox-sphere"> <label for="checkbox-sphere">sphere</label>
                <input type="checkbox" id="checkbox-triangles"> <label for="checkbox-triangles">triangles</label>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="main-canvas"></canvas>
            <button id="btn-reset-view">+</button>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let scene, camera, renderer, controls;
        let doubleCheckbox, sphereCheckbox, trianglesCheckbox;
        let sliderTrim, labelTrimValue;
        let radioX, radioY;
        let mainCanvas;

        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('main-canvas'),
                antialias: true
            });
            renderer.setSize(window.innerWidth, window.innerHeight);
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 0, 0);
            controls.update();

            doubleCheckbox = document.getElementById('checkbox-double');
            sphereCheckbox = document.getElementById('checkbox-sphere');
            trianglesCheckbox = document.getElementById('checkbox-triangles');
            sliderTrim = document.getElementById('slider-trim');
            labelTrimValue = document.getElementById('label-trim-value');
            radioX = document.querySelectorAll('input[name="unitsX"]');
            radioY = document.querySelectorAll('input[name="unitsY"]');
            mainCanvas = document.getElementById('main-canvas');
            document.getElementById('btn-reset-view').addEventListener('click', resetView);

            sliderTrim.addEventListener('input', updateTrimValue);
            radioX.forEach(radio => radio.addEventListener('change', updateScene));
            radioY.forEach(radio => radio.addEventListener('change', updateScene));
            doubleCheckbox.addEventListener('change', updateScene);
            sphereCheckbox.addEventListener('change', updateScene);
            trianglesCheckbox.addEventListener('change', updateScene);

            updateScene();

            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function resetView() {
            controls.reset();
        }

        function updateTrimValue() {
            labelTrimValue.textContent = sliderTrim.value;
            updateScene();
        }

        function updateScene() {
            scene.clear();

            let trimValue = parseFloat(sliderTrim.value);
            let unitsX = parseInt(radioX.find(radio => radio.checked).value);
            let unitsY = parseInt(radioY.find(radio => radio.checked).value);

            let geometry = generateGeometry(trimValue, unitsX, unitsY);
            let material = new THREE.MeshStandardMaterial({
                vertexColors: true
            });
            let mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);

            if (doubleCheckbox.checked) {
                let doubleGeometry = generateDoubleGeometry(trimValue, unitsX, unitsY);
                let doubleMaterial = new THREE.MeshStandardMaterial({
                    vertexColors: true,
                    color: 0x80ffff
                });
                let doubleMesh = new THREE.Mesh(doubleGeometry, doubleMaterial);
                doubleMesh.position.x = -unitsX * 2;
                doubleMesh.position.z = -unitsY * 2;
                doubleMesh.rotation.y = Math.PI;
                scene.add(doubleMesh);
            }

            if (sphereCheckbox.checked) {
                let sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
                let sphereMaterial = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80
                });
                let sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphereMesh.position.set(0, -1, 0);
                scene.add(sphereMesh);
            }

            if (trianglesCheckbox.checked) {
                let trianglesGeometry = generateTrianglesGeometry(trimValue, unitsX, unitsY);
                let trianglesMaterial = new THREE.MeshBasicMaterial({
                    color: 0x800080,
                    transparent: true,
                    opacity: 0.4
                });
                let trianglesMesh = new THREE.Mesh(trianglesGeometry, trianglesMaterial);
                scene.add(trianglesMesh);
                let pointsGeometry = generatePointsGeometry(trimValue, unitsX, unitsY);
                let pointsMaterial = new THREE.PointsMaterial({
                    color: 0x000000,
                    size: 3
                });
                let pointsMesh = new THREE.Points(pointsGeometry, pointsMaterial);
                scene.add(pointsMesh);
            }

            camera.position.set(0, 0, 5);
            camera.lookAt(0, 0, 0);
            controls.update();
            renderer.render(scene, camera);
        }

        function generateGeometry(trimValue, unitsX, unitsY) {
            let geometry = new THREE.BufferGeometry();
            let vertices = [];
            let colors = [];
            let indices = [];

            let basisVectors = [
                new THREE.Vector3(unitsX, 0, 0),
                new THREE.Vector3(0, unitsY, 0)
            ];

            for (let i = 0; i < unitsX; i++) {
                for (let j = 0; j < unitsY; j++) {
                    let position = new THREE.Vector3(i * basisVectors[0].x, j * basisVectors[1].y, 0);
                    let normal = new THREE.Vector3(0, 0, 1).clone().applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), Math.PI / 2)));
                    let vertex = new THREE.Vector3(position.x, position.y, position.z).applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(normal, trimValue)));
                    vertices.push(vertex.x, vertex.y, vertex.z);

                    let color = new THREE.Color();
                    color.setHSL(i / unitsX, 1, j / unitsY);
                    colors.push(color.r, color.g, color.b);

                    if (i > 0 && j > 0) {
                        indices.push((i - 1) + (j - 1) * unitsX, i + (j - 1) * unitsX, i + j * unitsX);
                    }
                }
            }

            geometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(vertices), 3));
            geometry.setAttribute('color', new THREE.BufferAttribute(new Float32Array(colors), 3));
            geometry.setIndex(new THREE.BufferAttribute(new Uint16Array(indices), 1));

            return geometry;
        }

        function generateDoubleGeometry(trimValue, unitsX, unitsY) {
            let geometry = new THREE.BufferGeometry();
            let vertices = [];
            let colors = [];
            let indices = [];

            let basisVectors = [
                new THREE.Vector3(unitsX, 0, 0),
                new THREE.Vector3(0, unitsY, 0)
            ];

            for (let i = 0; i < unitsX; i++) {
                for (let j = 0; j < unitsY; j++) {
                    let position = new THREE.Vector3(i * basisVectors[0].x, j * basisVectors[1].y, 0);
                    let normal = new THREE.Vector3(0, 0, 1).clone().applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), Math.PI / 2)));
                    let vertex = new THREE.Vector3(position.x, position.y, position.z).applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(normal, trimValue)));
                    vertices.push(vertex.x, vertex.y, vertex.z);

                    let color = new THREE.Color();
                    color.setHSL(i / unitsX, 1, j / unitsY);
                    colors.push(color.r, color.g, color.b);

                    if (i > 0 && j > 0) {
                        indices.push((i - 1) + (j - 1) * unitsX + unitsX * unitsY, i + (j - 1) * unitsX + unitsX * unitsY, i + j * unitsX + unitsX * unitsY);
                    }
                }
            }

            geometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(vertices), 3));
            geometry.setAttribute('color', new THREE.BufferAttribute(new Float32Array(colors), 3));
            geometry.setIndex(new THREE.BufferAttribute(new Uint16Array(indices), 1));

            return geometry;
        }

        function generateTrianglesGeometry(trimValue, unitsX, unitsY) {
            let geometry = new THREE.BufferGeometry();
            let vertices = [];
            let indices = [];

            let basisVectors = [
                new THREE.Vector3(unitsX, 0, 0),
                new THREE.Vector3(0, unitsY, 0)
            ];

            for (let i = 0; i < unitsX; i++) {
                for (let j = 0; j < unitsY; j++) {
                    let position = new THREE.Vector3(i * basisVectors[0].x, j * basisVectors[1].y, 0);
                    let normal = new THREE.Vector3(0, 0, 1).clone().applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), Math.PI / 2)));
                    let vertex = new THREE.Vector3(position.x, position.y, position.z).applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(normal, trimValue)));
                    vertices.push(vertex.x, vertex.y, vertex.z);

                    if (i > 0 && j > 0) {
                        indices.push((i - 1) + (j - 1) * unitsX, i + (j - 1) * unitsX, i + j * unitsX);
                    }
                }
            }

            geometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(vertices), 3));
            geometry.setIndex(new THREE.BufferAttribute(new Uint16Array(indices), 1));

            return geometry;
        }

        function generatePointsGeometry(trimValue, unitsX, unitsY) {
            let geometry = new THREE.BufferGeometry();
            let vertices = [];

            let basisVectors = [
                new THREE.Vector3(unitsX, 0, 0),
                new THREE.Vector3(0, unitsY, 0)
            ];

            for (let i = 0; i < unitsX; i++) {
                for (let j = 0; j < unitsY; j++) {
                    let position = new THREE.Vector3(i * basisVectors[0].x, j * basisVectors[1].y, 0);
                    let normal = new THREE.Vector3(0, 0, 1).clone().applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), Math.PI / 2)));
                    let vertex = new THREE.Vector3(position.x, position.y, position.z).applyQuaternion(new THREE.Quaternion().setFromRotationMatrix(new THREE.Matrix4().makeRotationAxis(normal, trimValue)));
                    vertices.push(vertex.x, vertex.y, vertex.z);
                }
            }

            geometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(vertices), 3));

            return geometry;
        }

        init();
    </script>
</body>
</html>
