</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-start-time">start time</label>
            <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
            <label for="slider-total-time">total time</label>
            <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
            <div id="function-buttons">
                <button id="btn-f" class="selected">f</button>
                <button id="btn-g">g</button>
                <button id="btn-sin">sin</button>
                <button id="btn-cos">cos</button>
                <button id="btn-tan">tan</button>
            </div>
            <label>encoding method</label>
            <div id="encoding-buttons">
                <button id="btn-asdm" class="selected">ASDM</button>
                <button id="btn-iaf">IAF</button>
            </div>
        </div>
        <div id="canvas-container">
            <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
            <script>
                let startTime = -1.0;
                let totalTime = 2.2;
                let functionIndex = 1; // g
                let encodingMethodIndex = 0; // ASDM

                function setup() {
                    createCanvas(800, 600);
                    background(220);
                    noLoop();
                }

                function draw() {
                    background(220);
                    drawCoordinateSystem();
                    drawAnalogSignal();
                    drawEncodedSignal();
                }

                function drawCoordinateSystem() {
                    stroke(0);
                    line(0, height / 2, width, height / 2);
                    line(width / 2, 0, width / 2, height);
                    for (let i = -5; i <= 5; i += 0.5) {
                        line(width / 2, height / 2 - (i * height / 5), width / 2, height / 2 + (i * height / 5));
                        line(width / 2 - (i * width / 10), height / 2, width / 2 + (i * width / 10), height / 2);
                    }
                }

                function drawAnalogSignal() {
                    let func;
                    switch (functionIndex) {
                        case 0:
                            func = (t) => 0.5 * t;
                            break;
                        case 1:
                            func = (t) => sin(2 * PI * t) + 0.3 * cos(3 * 2 * PI * t);
                            break;
                        case 2:
                            func = (t) => sin(2 * PI * t);
                            break;
                        case 3:
                            func = (t) => cos(2 * PI * t);
                            break;
                        case 4:
                            func = (t) => tan(PI * t);
                            break;
                    }
                    let yValues = [];
                    for (let t = startTime; t < startTime + totalTime; t += 0.01) {
                        yValues.push(func(t));
                    }
                    stroke(0, 0, 255);
                    beginShape();
                    for (let i = 0; i < yValues.length; i++) {
                        let x = map(i, 0, yValues.length, width / 2 - (totalTime * width / 2), width / 2 + (totalTime * width / 2));
                        let y = height / 2 + (yValues[i] * height / 2);
                        vertex(x, y);
                    }
                    endShape();
                }

                function drawEncodedSignal() {
                    let encodedValues;
                    switch (encodingMethodIndex) {
                        case 0:
                            encodedValues = drawASDM(startTime, totalTime, functionIndex);
                            break;
                        case 1:
                            encodedValues = drawIAF(startTime, totalTime, functionIndex);
                            break;
                    }
                    stroke(255, 0, 0);
                    beginShape();
                    for (let i = 0; i < encodedValues.length; i++) {
                        let x = map(i, 0, encodedValues.length, width / 2 - (totalTime * width / 2), width / 2 + (totalTime * width / 2));
                        let y = height / 2 + (encodedValues[i] * height / 2);
                        vertex(x, y);
                    }
                    endShape();
                }

                function drawASDM(startTime, totalTime, functionIndex) {
                    let outputLevels = [0.25, -0.25];
                    let flipTimes = [];
                    let currentTime = startTime;
                    let integral = 0;
                    let func;
                    switch (functionIndex) {
                        case 0:
                            func = (t) => 0.5 * t;
                            break;
                        case 1:
                            func = (t) => sin(2 * PI * t) + 0.3 * cos(3 * 2 * PI * t);
                            break;
                        case 2:
                            func = (t) => sin(2 * PI * t);
                            break;
                        case 3:
                            func = (t) => cos(2 * PI * t);
                            break;
                        case 4:
                            func = (t) => tan(PI * t);
                            break;
                    }
                    let dt = 0.01;
                    while (currentTime < startTime + totalTime) {
                        integral += (func(currentTime) - outputLevels[0]) * dt;
                        if (outputLevels[0] == 0.25 && integral < -0.05 || outputLevels[0] == -0.25 && integral > 0.05) {
                            flipTimes.push(currentTime);
                            integral = 0;
                            outputLevels[0] = -outputLevels[0];
                        }
                        currentTime += dt;
                    }
                    return flipTimes;
                }

                function drawIAF(startTime, totalTime, functionIndex) {
                    let spikeTimes = [];
                    let currentTime = startTime;
                    let integral = 0;
                    let func;
                    switch (functionIndex) {
                        case 0:
                            func = (t) => 0.5 * t;
                            break;
                        case 1:
                            func = (t) => sin(2 * PI * t) + 0.3 * cos(3 * 2 * PI * t);
                            break;
                        case 2:
                            func = (t) => sin(2 * PI * t);
                            break;
                        case 3:
                            func = (t) => cos(2 * PI * t);
                            break;
                        case 4:
                            func = (t) => tan(PI * t);
                            break;
                    }
                    let dt = 0.01;
                    while (currentTime < startTime + totalTime) {
                        integral += func(currentTime) * dt;
                        if (integral >= 0.05) {
                            spikeTimes.push(currentTime);
                            integral = 0;
                        }
                        currentTime += dt;
                    }
                    return spikeTimes;
                }

                function updateControls() {
                    startTime = parseFloat(document.getElementById("slider-start-time").value);
                    totalTime = parseFloat(document.getElementById("slider-total-time").value);
                    functionIndex = Array.prototype.indexOf.call(document.getElementById("function-buttons").children, document.querySelector("#function-buttons .selected"));
                    encodingMethodIndex = Array.prototype.indexOf.call(document.getElementById("encoding-buttons").children, document.querySelector("#encoding-buttons .selected"));
                    redraw();
                }

                function updateSelectedButton(button) {
                    let prevSelectedButton = document.querySelector("#function-buttons .selected");
                    prevSelectedButton.classList.remove("selected");
                    button.classList.add("selected");
                    functionIndex = Array.prototype.indexOf.call(document.getElementById("function-buttons").children, button);
                    encodingMethodIndex = Array.prototype.indexOf.call(document.getElementById("encoding-buttons").children, document.querySelector("#encoding-buttons .selected"));
                    redraw();
                }

                function updateSelectedEncodingButton(button) {
                    let prevSelectedButton = document.querySelector("#encoding-buttons .selected");
                    prevSelectedButton.classList.remove("selected");
                    button.classList.add("selected");
                    encodingMethodIndex = Array.prototype.indexOf.call(document.getElementById("encoding-buttons").children, button);
                    functionIndex = Array.prototype.indexOf.call(document.getElementById("function-buttons").children, document.querySelector("#function-buttons .selected"));
                    redraw();
                }

                function redraw() {
                    background(220);
                    drawCoordinateSystem();
                    drawAnalogSignal();
                    drawEncodedSignal();
                }

                setup();

                document.getElementById("slider-start-time").addEventListener("input", updateControls);
                document.getElementById("slider-total-time").addEventListener("input", updateControls);
                document.getElementById("function-buttons").addEventListener("click", (e) => {
                    if (e.target.classList.contains("selected")) return;
                    let button = e.target;
                    updateSelectedButton(button);
                });
                document.getElementById("encoding-buttons").addEventListener("click", (e) => {
                    if (e.target.classList.contains("selected")) return;
                    let button = e.target;
                    updateSelectedEncodingButton(button);
                });
                document.getElementById("info-button").addEventListener("click", () => {
                    let descriptionPanel = document.getElementById("description-panel");
                    descriptionPanel.style.display = descriptionPanel.style.display == "block" ? "none" : "block";
                });
            </script>
        </div>
        <div id="description-panel">
            <p>This is a demo for analog signal visualization. You can manipulate the start time, total time, function, and encoding method to see how they affect the plot.</p>
        </div>
        <button id="info-button">+</button>
    </div>
</body>
</html>
