            const waveType = button.id === 'p-wave-sine-button' ? 'sine' : 'pulse';
            button.classList.toggle('active');
            const otherButton = button.id === 'p-wave-sine-button' ? document.getElementById('p-wave-pulse-button') : document.getElementById('p-wave-sine-button');
            otherButton.classList.toggle('active');
        }

        // Function to handle S-wave direction button click
        function sWaveDirectionButtonClicked(button) {
            const sWaveDirection = button.id === 's-wave-dir-xy-button' ? 'xy' : 'z';
            button.classList.toggle('active');
            const otherButton = button.id === 's-wave-dir-xy-button' ? document.getElementById('s-wave-dir-z-button') : document.getElementById('s-wave-dir-xy-button');
            otherButton.classList.toggle('active');
        }

        // Function to handle viewpoint button click
        function viewpointButtonClicked(button) {
            const viewpoint = button.id === 'viewpoint-top-button' ? 'top' : 'default';
            button.classList.toggle('active');
            const otherButton = button.id === 'viewpoint-top-button' ? document.getElementById('viewpoint-default-button') : document.getElementById('viewpoint-top-button');
            otherButton.classList.toggle('active');
        }

        // Initialize scene, camera, and renderer
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xE0E0E0);
        camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('canvas'),
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);

        // Create geometry and material
        geometry = new THREE.PlaneBufferGeometry(10, 10, 40, 40);
        material = new THREE.MeshLambertMaterial({
            vertexColors: true
        });

        // Create mesh and line segments
        mesh = new THREE.Mesh(geometry, material);
        lineSegments = new THREE.LineSegments(geometry, new THREE.LineBasicMaterial({
            color: 0x556B2F
        }));

        // Add mesh and line segments to scene
        scene.add(mesh);
        scene.add(lineSegments);

        // Add camera and light to scene
        scene.add(camera);
        const ambientLight = new THREE.AmbientLight(0x444444);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(10, 10, 10);
        scene.add(directionalLight);

        // Add event listeners
        document.getElementById('play-pause-button').addEventListener('click', playPauseButtonClicked);
        document.getElementById('reset-button').addEventListener('click', resetButtonClicked);
        document.getElementById('step-button').addEventListener('click', stepButtonClicked);
        document.getElementById('plot-points-slider').addEventListener('input', plotPointsSliderChanged);
        document.getElementById('p-wave-sine-button').addEventListener('click', () => waveTypeButtonClicked(document.getElementById('p-wave-sine-button')));
        document.getElementById('p-wave-pulse-button').addEventListener('click', () => waveTypeButtonClicked(document.getElementById('p-wave-pulse-button')));
        document.getElementById('s-wave-sine-button').addEventListener('click', () => waveTypeButtonClicked(document.getElementById('s-wave-sine-button')));
        document.getElementById('s-wave-pulse-button').addEventListener('click', () => waveTypeButtonClicked(document.getElementById('s-wave-pulse-button')));
        document.getElementById('s-wave-dir-xy-button').addEventListener('click', () => sWaveDirectionButtonClicked(document.getElementById('s-wave-dir-xy-button')));
        document.getElementById('s-wave-dir-z-button').addEventListener('click', () => sWaveDirectionButtonClicked(document.getElementById('s-wave-dir-z-button')));
        document.getElementById('viewpoint-top-button').addEventListener('click', () => viewpointButtonClicked(document.getElementById('viewpoint-top-button')));
        document.getElementById('viewpoint-default-button').addEventListener('click', () => viewpointButtonClicked(document.getElementById('viewpoint-default-button')));

        // Start animation loop
        animate();
    </script>
</body>
</html>
