    <script>
        let a, b, c, z, showLabels, showCircle, showAxes, showGridLines;
        let canvas, plotRange;
        let L1, L2, L3, L1p, L2p;

        function setup() {
            createCanvas(800, 600);
            canvas = createDiv('');
