            showGridLines = false;
            a = 1; b = -1; c = -1;
            z = 1.5;
            L1 = createVector(a, 0);
            L2 = createVector(a, b);
            L3 = createVector(a - c, b);
            L1p = createVector(a, -z*a);
            L2p = createVector(a + b*z + a*z*z, b);
            draw();
        }

        function draw() {
            background(220);
            if (showAxes) {
                stroke(0); strokeWeight(2);
                line(0, height/2, width, height/2);
                line(width/2, 0, width/2, height);
