                for (let y = 0; y < height; y += 50) {
                    line(0, y, width, y);
                }
            }
            if (showCircle) {
                stroke(0); strokeWeight(2);
                fill(255, 0, 0, 100);
                ellipse(width/2, height/2, 100, 100);
            }
            if (showLabels) {
                fill(0); textSize(16);
                text('O', L1.x, L1.y + 20);
                text('L1', L2.x, L2.y + 20);
                text('L2', L3.x, L3.y + 20);
            }
            stroke(0); strokeWeight(2);
            line(L1.x, L1.y, L2.x, L2.y);
            line(L2.x, L2.y, L3.x, L3.y);
            fill(0); noStroke();
            ellipse(L1.x, L1.y, 10, 10);
            ellipse(L2.x, L2.y, 10, 10);
            ellipse(L3.x, L3.y, 10, 10);
            stroke(255, 0, 0); strokeWeight(2);
            line(L1p.x, L1p.y, L2p.x, L2p.y);
            fill(255, 0, 0, 100);
            ellipse(L1p.x, L1p.y, 10, 10);
            ellipse(L2p.x, L2p.y, 10, 10);
            stroke(0); strokeWeight(2);
            line(L2p.x, L2p.y, L3.x, L3.y);
            fill(0); noStroke();
            ellipse(L2p.x, L2p.y, 10, 10);
            let pz = a*z*z + b*z + c;
            let l3l2 = dist(L3.x, L3.y, L2p.x, L2p.y);
            let valuePz = nf(pz, 2, 4);
            let valueL3L2 = nf(l3l2, 2, 4);
            let valueZ = nf(z, 2, 4);
            document.getElementById('value-pz').textContent = valuePz;
            document.getElementById('value-l3l2').textContent = valueL3L2;
            document.getElementById('value-z').textContent = valueZ;
        }

        function update() {
            let newZ = document.getElementById('slider-z').value;
            z = parseFloat(newZ);
            let newPlotRange = document.getElementById('slider-plot-range').value;
            plotRange = parseFloat(newPlotRange);
            showLabels = document.getElementById('check-show-labels').checked;
            showCircle = document.getElementById('check-show-circle').checked;
            showAxes = document.getElementById('check-show-axes').checked;
            showGridLines = document.getElementById('check-show-grid-lines').checked;
            L1p = createVector(a, -z*a);
            L2p = createVector(a + b*z + a*z*z, b);
            let pz = a*z*z + b*z + c;
            let l3l2 = dist(L3.x, L3.y, L2p.x, L2p.y);
            let valuePz = nf(pz, 2, 4);
            let valueL3L2 = nf(l3l2, 2, 4);
            let valueZ = nf(z, 2, 4);
            document.getElementById('value-pz').textContent = valuePz;
            document.getElementById('value-l3l2').textContent = valueL3L2;
            document.getElementById('value-z').textContent = valueZ;
            canvas.html('');
            draw();
        }

        function newExample() {
            a = Math.floor(Math.random() * 7) - 3;
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;
            z = 1.5;
