            L1p = createVector(a, -z*a);
            L2p = createVector(a + b*z + a*z*z, b);
            let formulaText = 'z^2 + ' + nf(b, 2, 4) + 'z + ' + nf(c, 2, 4);
            document.getElementById('formula-text').textContent = formulaText;
            MathJax.typesetPromise().then(() => {
                canvas.html('');
                draw();
            });
        }

        function goldenRatio() {
            a = 1;
            b = -1;
            c = -1;
            z = 1.5;
            L1 = createVector(a, 0);
            L2 = createVector(a, b);
            L3 = createVector(a - c, b);
            L1p = createVector(a, -z*a);
            L2p = createVector(a + b*z + a*z*z, b);
            let formulaText = 'z^2 - z - 1';
            document.getElementById('formula-text').textContent = formulaText;
            MathJax.typesetPromise().then(() => {
                canvas.html('');
                draw();
            });
        }

        document.getElementById('slider-z').addEventListener('input', update);
        document.getElementById('slider-plot-range').addEventListener('input', update);
        document.getElementById('check-show-labels').addEventListener('change', update);
        document.getElementById('check-show-circle').addEventListener('change', update);
        document.getElementById('check-show-axes').addEventListener('change', update);
        document.getElementById('check-show-grid-lines').addEventListener('change', update);
        document.getElementById('btn-new-example').addEventListener('click', newExample);
        document.getElementById('btn-golden-ratio').addEventListener('click', goldenRatio);

        setup();
    </script>
</body>
</html>
