    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let currentView = 'plot_solutions';

        let solutionCurveData = [
            { alpha: 0.000, x1: 0.000, x2: 0.000, t1: 0.000, t2: 0.000 },
            { alpha: 0.001, x1: 0.001, x2: 0.001, t1: 0.001, t2: 0.001 },
            { alpha: 0.002, x1: 0.002, x2: 0.002, t1: 0.002, t2: 0.002 },
            { alpha: 0.003, x1: 0.003, x2: 0.003, t1: 0.003, t2: 0.003 },
            { alpha: 0.004, x1: 0.004, x2: 0.004, t1: 0.004, t2: 0.004 },
            { alpha: 0.005, x1: 0.005, x2: 0.005, t1: 0.005, t2: 0.005 },
            { alpha: 0.006, x1: 0.006, x2: 0.006, t1: 0.006, t2: 0.006 },
            { alpha: 0.007, x1: 0.007, x2: 0.007, t1: 0.007, t2: 0.007 },
        ];

        let turningPointsData = [
            { alpha: 0.002, t2: 0.002 },
            { alpha: 0.003, t2: 0.003 },
            { alpha: 0.004, t2: 0.004 },
            { alpha: 0.005, t2: 0.005 },
            { alpha: 0.006, t2: 0.006 },
            { alpha: 0.007, t2: 0.007 },
        ];

        let sliderAlpha = document.getElementById('slider-alpha');
        let alphaValueDisplay = document.getElementById('alpha-value-display');
        let plotView = document.getElementById('plot-view');
        let tableView = document.getElementById('table-view');
        let solutionsTableBody = document.getElementById('solutions-table-body');

        sliderAlpha.addEventListener('input', () => {
            alphaValueDisplay.textContent = parseFloat(sliderAlpha.value).toFixed(4);
            if (currentView === 'plot_solutions') {
                plotView.innerHTML = '';
                draw();
            } else if (currentView === 'table') {
                solutionsTableBody.innerHTML = '';
                updateTable();
            }
        });

        document.getElementById('btn-plot').addEventListener('click', () => {
            currentView = 'plot_solutions';
            document.getElementById('btn-plot').classList.add('active');
            document.getElementById('btn-table').classList.remove('active');
            document.getElementById('btn-turning-points').classList.remove('active');
            plotView.style.display = 'block';
            tableView.style.display = 'none';
            document.getElementById('parameter-controls').style.display = 'block';
        });

        document.getElementById('btn-table').addEventListener('click', () => {
            currentView = 'table';
            document.getElementById('btn-plot').classList.remove('active');
            document.getElementById('btn-table').classList.add('active');
            document.getElementById('btn-turning-points').classList.remove('active');
            plotView.style.display = 'none';
            tableView.style.display = 'block';
            document.getElementById('parameter-controls').style.display = 'block';
            updateTable();
        });

        document.getElementById('btn-turning-points').addEventListener('click', () => {
            currentView = 'plot_turning_points';
            document.getElementById('btn-plot').classList.remove('active');
            document.getElementById('btn-table').classList.remove('active');
            document.getElementById('btn-turning-points').classList.add('active');
            plotView.style.display = 'block';
            tableView.style.display = 'none';
            document.getElementById('parameter-controls').style.display = 'none';
            plotView.innerHTML = '';
            draw();
        });

        function draw() {
            let canvas = createCanvas(600, 600);
            canvas.parent('plot-view');

            let xScale = map(0, 0.07, 0, width, 0);
            let yScale = map(0, 8, 0, height, 0);

            background(255);

            stroke(0);
            line(xScale, 0, xScale, height);
            line(0, yScale, width, yScale);

            stroke(128);
            for (let i = 0; i < height; i += 20) {
                line(0, i, width, i);
            }
            for (let i = 0; i < width; i += 20) {
                line(i, 0, i, height);
            }

            stroke(255, 0, 0);
            strokeWeight(2);
            beginShape();
            for (let i = 0; i < solutionCurveData.length; i++) {
                let x = map(solutionCurveData[i].alpha, 0, 0.07, 0, width);
                let y = map(solutionCurveData[i].t2, 0, 8, height, 0);
                vertex(x, y);
            }
            endShape();

            if (currentView === 'plot_solutions') {
                stroke(0, 255, 0);
                strokeWeight(1);
                for (let i = 0; i < solutionCurveData.length - 1; i++) {
                    let x1 = map(solutionCurveData[i].alpha, 0, 0.07, 0, width);
                    let y1 = map(solutionCurveData[i].t2, 0, 8, height, 0);
                    let x2 = map(solutionCurveData[i + 1].alpha, 0, 0.07, 0, width);
                    let y2 = map(solutionCurveData[i + 1].t2, 0, 8, height, 0);
                    let alpha = (solutionCurveData[i].alpha + solutionCurveData[i + 1].alpha) / 2;
                    let t2 = (solutionCurveData[i].t2 + solutionCurveData[i + 1].t2) / 2;
                    let x = map(alpha, 0, 0.07, 0, width);
                    let y = map(t2, 0, 8, height, 0);
                    line(x1, y1, x, y);
                    line(x, y, x2, y2);
                }

                let alpha = parseFloat(sliderAlpha.value);
                let t2 = 0;
                let found = false;
                for (let i = 0; i < solutionCurveData.length - 1; i++) {
                    if (solutionCurveData[i].alpha <= alpha && solutionCurveData[i + 1].alpha >= alpha) {
                        let x1 = map(solutionCurveData[i].alpha, 0, 0.07, 0, width);
                        let y1 = map(solutionCurveData[i].t2, 0, 8, height, 0);
                        let x2 = map(solutionCurveData[i + 1].alpha, 0, 0.07, 0, width);
                        let y2 = map(solutionCurveData[i + 1].t2, 0, 8, height, 0);
                        let x = map(alpha, 0, 0.07, 0, width);
                        let y = map(t2, 0, 8, height, 0);
                        found = true;
                        break;
                    }
                }
                if (found) {
                    stroke(0, 0, 255);
                    strokeWeight(4);
                    line(x, y, x, y + 10);
                }

                stroke(0, 0, 255);
                strokeWeight(4);
                for (let i = 0; i < solutionCurveData.length; i++) {
                    let x = map(solutionCurveData[i].alpha, 0, 0.07, 0, width);
                    let y = map(solutionCurveData[i].t2, 0, 8, height, 0);
                    ellipse(x, y, 10, 10);
                }
            } else if (currentView === 'plot_turning_points') {
                stroke(0, 255, 0);
                strokeWeight(4);
                for (let i = 0; i < turningPointsData.length; i++) {
                    let x = map(turningPointsData[i].alpha, 0, 0.07, 0, width);
                    let y = map(turningPointsData[i].t2, 0, 8, height, 0);
                    ellipse(x, y, 10, 10);
                }
            }
        }

        function updateTable() {
            let alpha = parseFloat(sliderAlpha.value);
            let solutions = [];
            for (let i = 0; i < solutionCurveData.length; i++) {
                if (solutionCurveData[i].alpha <= alpha && solutionCurveData[i + 1].alpha >= alpha) {
                    solutions.push({
                        root: i,
                        x1: solutionCurveData[i].x1,
                        x2: solutionCurveData[i].x2,
                        t1: solutionCurveData[i].t1,
                        t2: solutionCurveData[i].t2,
                    });
                }
            }
            solutionsTableBody.innerHTML = '';
            for (let i = 0; i < solutions.length; i++) {
                let row = solutionsTableBody.insertRow();
                let rootCell = row.insertCell();
                rootCell.textContent = solutions[i].root;
                let x1Cell = row.insertCell();
                x1Cell.textContent = solutions[i].x1.toFixed(5);
                let x2Cell = row.insertCell();
                x2Cell.textContent = solutions[i].x2.toFixed(5);
                let t1Cell = row.insertCell();
                t1Cell.textContent = solutions[i].t1.toFixed(5);
                let t2Cell = row.insertCell();
                t2Cell.textContent = solutions[i].t2.toFixed(5);
            }
