    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };

        let dragging = { z0: false, plus: false, minus: false };

        let slider = document.getElementById('slider-growth');
        let displayGrowth = document.getElementById('display-growth');
        let resetGrowth = document.getElementById('btn-reset-growth');

        let canvas = document.getElementById('p5-canvas');
        let ctx = canvas.getContext('2d');

        function setup() {
            createCanvas(800, 600);
            background(220);
            noStroke();
            frameRate(30);
        }

        function draw() {
            background(220);

            if (spiralType === 'double') {
                drawDoubleSpiral();
            } else {
                drawSingleSpiral();
            }

            drawLocators();
        }

        function drawSingleSpiral() {
            let k = spiralGrowth;
            let t = [-8 * PI, 8 * PI];
            let x = [];
            let y = [];

            for (let i = 0; i < 100; i++) {
                let z = { x: z0_loc.x + Math.exp(k * t[i]) * Math.cos(t[i]), y: z0_loc.y - Math.exp(k * t[i]) * Math.sin(t[i]) };
                x.push(z.x);
                y.push(z.y);
            }

            beginShape();
            curveVertex(x[0], y[0]);
            for (let i = 1; i < 100; i++) {
                curveVertex(x[i], y[i]);
            }
            curveVertex(x[99], y[99]);
            endShape();

            if (itemType === 'point') {
                for (let i = 1; i < 100; i++) {
                    fill(255, 0, 0);
                    ellipse(x[i], y[i], 5);
                }
            } else if (itemType === 'fox') {
                for (let i = 1; i < 100; i++) {
                    push();
                    translate(x[i], y[i]);
                    rotate(atan2(imag(z0_loc), real(z0_loc)));
                    fill(255, 165, 0);
                    noStroke();
                    beginShape();
                    vertex(-15, 0);
                    vertex(-10, -5);
                    vertex(-5, -4);
                    vertex(0, -5);
                    vertex(5, -5);
                    vertex(8, -2);
                    vertex(10, -6);
                    vertex(12, -6);
                    vertex(15, 0);
                    vertex(12, 6);
                    vertex(10, 6);
                    vertex(8, 2);
                    vertex(5, 5);
                    vertex(0, 5);
                    vertex(-5, 4);
                    vertex(-10, 5);
                    endShape(CLOSE);
                    pop();
                }
            } else if (itemType === 'filled-fox') {
                for (let i = 1; i < 100; i++) {
                    push();
                    translate(x[i], y[i]);
                    rotate(atan2(imag(z0_loc), real(z0_loc)));
                    fill(255, 165, 0);
                    noStroke();
                    beginShape();
                    vertex(-15, 0);
                    vertex(-10, -5);
                    vertex(-5, -4);
                    vertex(0, -5);
                    vertex(5, -5);
                    vertex(8, -2);
                    vertex(10, -6);
                    vertex(12, -6);
                    vertex(15, 0);
                    vertex(12, 6);
                    vertex(10, 6);
                    vertex(8, 2);
                    vertex(5, 5);
                    vertex(0, 5);
                    vertex(-5, 4);
                    vertex(-10, 5);
                    endShape(CLOSE);
                    pop();
                }
            }
        }

        function drawDoubleSpiral() {
            let k = spiralGrowth;
            let p = z_plus_loc;
            let q = z_minus_loc;
            let t = [-8 * PI, 8 * PI];
            let w = [];
            let z = [];

            for (let i = 0; i < 100; i++) {
                let w_i = { x: Math.exp(k * t[i]), y: 0 };
                w.push(w_i);
            }

            for (let i = 0; i < 100; i++) {
                let z_i = (p * w[i] - q) / (w[i] - 1);
                z.push(z_i);
            }

            beginShape();
            curveVertex(z[0].x, z[0].y);
            for (let i = 1; i < 100; i++) {
                curveVertex(z[i].x, z[i].y);
            }
            curveVertex(z[99].x, z[99].y);
            endShape();

            beginShape();
            curveVertex(z[0].x, z[0].y);
            for (let i = 1; i < 100; i++) {
                curveVertex(z[99 - i].x, z[99 - i].y);
            }
            curveVertex(z[99].x, z[99].y);
            endShape();

            if (itemType === 'point') {
                for (let i = 1; i < 100; i++) {
                    fill(255, 0, 0);
                    ellipse(z[i].x, z[i].y, 5);
                }
            } else if (itemType === 'fox') {
                for (let i = 1; i < 100; i++) {
                    push();
                    translate(z[i].x, z[i].y);
                    rotate(atan2(imag(z[i]) - imag(p), real(z[i]) - real(p)));
                    fill(255, 165, 0);
                    noStroke();
                    beginShape();
                    vertex(-15, 0);
                    vertex(-10, -5);
                    vertex(-5, -4);
                    vertex(0, -5);
                    vertex(5, -5);
                    vertex(8, -2);
                    vertex(10, -6);
                    vertex(12, -6);
                    vertex(15, 0);
                    vertex(12, 6);
                    vertex(10, 6);
                    vertex(8, 2);
                    vertex(5, 5);
                    vertex(0, 5);
                    vertex(-5, 4);
                    vertex(-10, 5);
                    endShape(CLOSE);
                    pop();
                }
            } else if (itemType === 'filled-fox') {
                for (let i = 1; i < 100; i++) {
                    push();
                    translate(z[i].x, z[i].y);
                    rotate(atan2(imag(z[i]) - imag(p), real(z[i]) - real(p)));
                    fill(255, 165, 0);
                    noStroke();
                    beginShape();
                    vertex(-15, 0);
                    vertex(-10, -5);
                    vertex(-5, -4);
                    vertex(0, -5);
                    vertex(5, -5);
                    vertex(8, -2);
                    vertex(10, -6);
                    vertex(12, -6);
                    vertex(15, 0);
                    vertex(12, 6);
                    vertex(10, 6);
                    vertex(8, 2);
                    vertex(5, 5);
                    vertex(0, 5);
                    vertex(-5, 4);
                    vertex(-10, 5);
                    endShape(CLOSE);
                    pop();
                }
            }

            fill(255);
            noStroke();
            textSize(16);
            textAlign(LEFT, TOP);
            text('z0Loc', (p.x + q.x) / 2, (p.y + q.y) / 2 - 20);
        }

        function drawLocators() {
            if (dragging.z0) {
                fill(255, 255, 0, 200);
                noStroke();
                ellipse(z0_loc.x, z0_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z0_loc.x, z0_loc.y, 10);
                line(z0_loc.x - 10, z0_loc.y - 10, z0_loc.x + 10, z0_loc.y + 10);
                line(z0_loc.x - 10, z0_loc.y + 10, z0_loc.x + 10, z0_loc.y - 10);
            } else {
                fill(255, 255, 0, 200);
                noStroke();
                ellipse(z0_loc.x, z0_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z0_loc.x, z0_loc.y, 10);
            }

            if (dragging.plus) {
                fill(0, 255, 0, 200);
                noStroke();
                ellipse(z_plus_loc.x, z_plus_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z_plus_loc.x, z_plus_loc.y, 10);
                line(z_plus_loc.x - 10, z_plus_loc.y - 10, z_plus_loc.x + 10, z_plus_loc.y + 10);
                line(z_plus_loc.x - 10, z_plus_loc.y + 10, z_plus_loc.x + 10, z_plus_loc.y - 10);
            } else {
                fill(0, 255, 0, 200);
                noStroke();
                ellipse(z_plus_loc.x, z_plus_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z_plus_loc.x, z_plus_loc.y, 10);
            }

            if (dragging.minus) {
                fill(0, 255, 0, 200);
                noStroke();
                ellipse(z_minus_loc.x, z_minus_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z_minus_loc.x, z_minus_loc.y, 10);
                line(z_minus_loc.x - 10, z_minus_loc.y - 10, z_minus_loc.x + 10, z_minus_loc.y + 10);
                line(z_minus_loc.x - 10, z_minus_loc.y + 10, z_minus_loc.x + 10, z_minus_loc.y - 10);
            } else {
                fill(0, 255, 0, 200);
                noStroke();
                ellipse(z_minus_loc.x, z_minus_loc.y, 20);
                fill(0);
                stroke(0);
                ellipse(z_minus_loc.x, z_minus_loc.y, 10);
            }
        }

        function mousePressed() {
            let x = mouseX - canvas.offsetLeft;
            let y = mouseY - canvas.offsetTop;

            if (dist(x, y, z0_loc.x, z0_loc.y) < 10) {
                dragging.z0 = true;
            } else if (dist(x, y, z_plus_loc.x, z_plus_loc.y) < 10) {
                dragging.plus = true;
            } else if (dist(x, y, z_minus_loc.x, z_minus_loc.y) < 10) {
                dragging.minus = true;
            }
        }

        function mouseDragged() {
            if (dragging.z0) {
                z0_loc.x = mouseX - canvas.offsetLeft;
                z0_loc.y = mouseY - canvas.offsetTop;
            } else if (dragging.plus) {
                z_plus_loc.x = mouseX - canvas.offsetLeft;
                z_plus_loc.y = mouseY - canvas.offsetTop;
            } else if (dragging.minus) {
                z_minus_loc.x = mouseX - canvas.offsetLeft;
                z_minus_loc.y = mouseY - canvas.offsetTop;
            }

            draw();
        }

        function mouseReleased() {
            dragging.z0 = false;
            dragging.plus = false;
            dragging.minus = false;
        }

        function updateGrowth() {
            let value = slider.value;
            spiralGrowth = parseFloat(value);
            displayGrowth.textContent = spiralGrowth.toFixed(4);
            draw();
        }

        function resetGrowth() {
            spiralGrowth = 0.25;
            slider.value = 0.25;
            displayGrowth.textContent = spiralGrowth.toFixed(4);
            draw();
        }

        slider.addEventListener('input', updateGrowth);
        resetGrowth.addEventListener('click', resetGrowth);

        setup();
        draw();
    </script>
</body>
