        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container">
            <canvas id="canvas"></canvas>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/dist/p5.js"></script>
    <script>
        let startVertex = 'm';
        let edgeWeights = {
            'm-n': 8, 'n-o': 7, 'o-p': 7,
            'i-j': 9, 'j-k': 7, 'k-l': 1,
            'e-f': 1, 'f-g': 7, 'g-h': 6,
            'a-b': 10, 'b-c': 4, 'c-d': 8,
            'm-i': 6, 'i-e': 9, 'e-a': 9,
            'n-j': 7, 'j-f': 6, 'f-b': 5,
            'o-k': 1, 'k-g': 8, 'g-c': 8,
            'p-l': 2, 'l-h': 1, 'h-d': 7
        };
        let distances = {};
        let visited = {};
        let shortestPathTree = {};
        let canvas;
        let graph;

        function setup() {
            canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            graph = new Graph(600, 600);
            graph.init();
            updateUI();
        }

        function draw() {
            graph.draw();
        }

        function updateUI() {
            document.getElementById('title-display').innerText = `Dijkstra's algorithm starting at vertex ${startVertex}`;
            let selectedButton = document.querySelector(`button.selected`);
            if (selectedButton) {
                selectedButton.classList.remove('selected');
            }
            document.querySelector(`button#${startVertex}`).classList.add('selected');
        }

        function resetAlgorithm() {
            startVertex = 'm';
            updateUI();
            for (let vertex in distances) {
                distances[vertex] = Infinity;
            }
            distances[startVertex] = 0;
            visited = {};
            shortestPathTree = {};
            updateUI();
            graph.reset();
        }

        function loadFixedExample() {
            edgeWeights = {
                'm-n': 8, 'n-o': 7, 'o-p': 7,
                'i-j': 9, 'j-k': 7, 'k-l': 1,
                'e-f': 1, 'f-g': 7, 'g-h': 6,
                'a-b': 10, 'b-c': 4, 'c-d': 8,
                'm-i': 6, 'i-e': 9, 'e-a': 9,
                'n-j': 7, 'j-f': 6, 'f-b': 5,
                'o-k': 1, 'k-g': 8, 'g-c': 8,
                'p-l': 2, 'l-h': 1, 'h-d': 7
            };
            resetAlgorithm();
        }

        function loadRandomExample() {
            edgeWeights = {};
            for (let i = 0; i < 24; i++) {
                let vertex1 = String.fromCharCode(97 + Math.floor(i / 6));
                let vertex2 = String.fromCharCode(97 + (i % 6));
                let weight = Math.floor(Math.random() * 10) + 1;
                edgeWeights[`${vertex1}-${vertex2}`] = weight;
            }
            resetAlgorithm();
        }

        function nextStep() {
            let unvisitedVertices = Object.keys(distances).filter(vertex => !visited[vertex]);
            let nextVertex = unvisitedVertices.reduce((minVertex, vertex) => distances[vertex] < distances[minVertex] ? vertex : minVertex, unvisitedVertices[0]);
            visited[nextVertex] = true;
            for (let neighbor in edgeWeights) {
                if (neighbor.startsWith(nextVertex)) {
                    let weight = edgeWeights[neighbor];
                    let distance = distances[nextVertex] + weight;
                    if (distance < distances[neighbor.split('-')[1]]) {
                        distances[neighbor.split('-')[1]] = distance;
                        shortestPathTree[neighbor.split('-')[1]] = nextVertex;
                    }
                }
            }
            updateUI();
            graph.update();
            if (Object.keys(visited).length === 16) {
                document.getElementById('next-step-btn').disabled = true;
            }
        }

        document.getElementById('start-over-btn').addEventListener('click', resetAlgorithm);
        document.getElementById('fixed-example-btn').addEventListener('click', loadFixedExample);
        document.getElementById('random-example-btn').addEventListener('click', loadRandomExample);
        document.getElementById('next-step-btn').addEventListener('click', nextStep);

        setup();
    </script>
</body>
</html>
