            scene.add(light);
            let directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);
            directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(-1, -1, -1);
