            scene.clear();
            let activeButton = document.querySelector('#polyhedron-buttons .active');
            let polyhedronName = activeButton ? activeButton.textContent.replace(' ', '') : 'octahedron';
            let geometry;
            switch (polyhedronName) {
                case 'cube':
                    geometry = new THREE.BoxGeometry(2, 2, 2);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(2);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(2);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(2);
                    break;
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(2);
                    break;
            }
            let vertices = geometry.attributes.position.array;
            let coneHeight = document.getElementById('slider-cone-height').value;
            let coneBase = document.getElementById('slider-cone-base').value;
            for (let i = 0; i < vertices.length; i += 3) {
                let vertex = new THREE.Vector3(vertices[i], vertices[i + 1], vertices[i + 2]);
                let direction = vertex.normalize();
                let position = direction.clone().multiplyScalar(1.5 + coneHeight / 2);
                let cone = new THREE.Mesh(new THREE.ConeGeometry(coneBase, coneHeight, 32), new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 }));
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                scene.add(cone);
            }
            scene.add(sphere);
            camera.position.set(0, 0, 8);
            controls.target.set(0, 0, 0);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>
