                    <input type="range" id="slider-tilt" value="23.5" min="0" max="90" step="0.1">
                </div>
                <div>
                    <label for="slider-equinox">Spring equinox point</label>
                    <input type="range" id="slider-equinox" value="0" min="-180" max="180" step="1">
                </div>
                <div>
                    <label for="slider-eccentricity">Orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" value="0.1" min="0" max="0.5" step="0.001">
                </div>
            </div>
            <div id="display-controls">
                <input type="checkbox" id="checkbox-snapshots" checked>
                <label for="checkbox-snapshots">Show sun snapshots</label>
                <input type="checkbox" id="checkbox-scales" checked>
                <label for="checkbox-scales">Show scales</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="analemma-canvas" width="600" height="600"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let selectedPlanet = "manual";
        let manualControl = false;
        let tilt = 23.5;
        let eccentricity = 0.1;
        let equinox = 0;
        let showSnapshots = true;
        let showScales = true;

        function setup() {
            createCanvas(600, 600);
            angleMode(DEGREES);
            background(255);
            frameRate(30);
        }

        function draw() {
            background(255);
            let xScale = 200;
            let yScale = 200;

            if (manualControl) {
                tilt = document.getElementById("slider-tilt").value;
                eccentricity = document.getElementById("slider-eccentricity").value;
                equinox = document.getElementById("slider-equinox").value;
            } else {
                let planet = selectedPlanet;
                tilt = planetData[planet].tilt;
                eccentricity = planetData[planet].eccentricity;
                equinox = planetData[planet].equinox;
            }

            let points = [];
            for (let d = 0; d < 365; d++) {
                let M = (2 * Math.PI * d) / 365.25;
                let E = kepler(M, eccentricity);
                let v = 2 * Math.atan2(Math.sqrt(1 + eccentricity) * Math.sin(E / 2), Math.sqrt(1 - eccentricity) * Math.cos(E / 2));
                let lambda = v + equinox;
                let y = Math.asin(Math.sin(tilt) * Math.sin(lambda));
                let x = (M - Math.atan2(Math.cos(tilt) * Math.sin(lambda), Math.cos(lambda))) * xScale;
                points.push({ x, y });
            }

            if (showScales) {
                stroke(0);
                line(0, height / 2, width, height / 2);
                line(width / 2, 0, width / 2, height);
                for (let i = -40; i <= 40; i += 10) {
                    line(width / 2, height / 2 - i * 20, width / 2 - 5, height / 2 - i * 20);
                    line(width / 2, height / 2 - i * 20, width / 2 + 5, height / 2 - i * 20);
                    text(i, width / 2 - 10, height / 2 - i * 20);
                    text(i, width / 2 + 10, height / 2 - i * 20);
                }
            }

            if (showSnapshots) {
                stroke(255, 165, 0);
                strokeWeight(2);
                beginShape();
                for (let i = 0; i < points.length; i++) {
                    if (i % 20 === 0) {
                        vertex(points[i].x, points[i].y);
                    }
                }
                endShape();
            }

            stroke(0);
            noFill();
            beginShape();
            for (let i = 0; i < points.length; i++) {
                vertex(points[i].x, points[i].y);
            }
            endShape();

            if (selectedPlanet !== "manual") {
                let planet = selectedPlanet;
                tilt = planetData[planet].tilt;
                eccentricity = planetData[planet].eccentricity;
                equinox = planetData[planet].equinox;
                manualControl = false;
            }
        }

        function kepler(M, e) {
            let E = M;
            while (true) {
                let E_new = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                if (abs(E_new - E) < 0.0001) {
                    break;
                }
                E = E_new;
            }
            return E;
        }

        document.getElementById("select-planet").addEventListener("change", function() {
            selectedPlanet = this.value;
            manualControl = selectedPlanet === "manual";
            if (selectedPlanet !== "manual") {
                let planet = selectedPlanet;
                tilt = planetData[planet].tilt;
                eccentricity = planetData[planet].eccentricity;
                equinox = planetData[planet].equinox;
                manualControl = false;
            }
            redraw();
        });

        document.getElementById("checkbox-manual").addEventListener("change", function() {
            manualControl = this.checked;
            if (manualControl) {
                document.getElementById("select-planet").value = "manual";
                selectedPlanet = "manual";
            } else {
                let planet = selectedPlanet;
                tilt = planetData[planet].tilt;
                eccentricity = planetData[planet].eccentricity;
                equinox = planetData[planet].equinox;
            }
            redraw();
        });

        document.getElementById("slider-tilt").addEventListener("input", function() {
            tilt = this.value;
            redraw();
        });

        document.getElementById("slider-equinox").addEventListener("input", function() {
            equinox = this.value;
            redraw();
        });

        document.getElementById("slider-eccentricity").addEventListener("input", function() {
            eccentricity = this.value;
            redraw();
        });

        document.getElementById("checkbox-snapshots").addEventListener("change", function() {
            showSnapshots = this.checked;
            redraw();
        });

        document.getElementById("checkbox-scales").addEventListener("change", function() {
            showScales = this.checked;
            redraw();
        });

        setup();
    </script>
</body>
</html>
