                let path = [];
                let totalWeight = 0;
                let visited = {};
                visited[edges[0].from] = true;
                path.push(edges[0]);
                totalWeight += edges[0].weight;
                let currentVertex = edges[0].from;

                while (true) {
                    let nextEdges = edges.filter(function (edge) {
                        return edge.from === currentVertex && !visited[edge.to];
                    });
                    if (nextEdges.length === 0) {
