                    p5Algo.strokeWeight(3);
                    p5Algo.line(x1 * 50 + 50, y1 * 50 + 50, x2 * 50 + 50, y2 * 50 + 50);
                }
                nextStepButton.disabled = true;
                if (path.length > 1) {
                    nextStepButton.disabled = false;
                }
            }

            function findOptimalPath(graph) {
                let vertices = graph.vertices;
                let edges = graph.edges;
                let optimalPath = null;
                let minWeight = Infinity;

                for (let i = 0; i < vertices.length; i++) {
                    let currentVertex = vertices[i];
                    let visited = {};
                    visited[currentVertex] = true;
                    let path = [];
                    let totalWeight = 0;

                    while (true) {
                        let nextEdges = edges.filter(function (edge) {
                            return edge.from === currentVertex && !visited[edge.to];
                        });
                        if (nextEdges.length === 0) {
                            break;
                        }
                        let nextEdge = nextEdges[0];
                        path.push(nextEdge);
                        totalWeight += nextEdge.weight;
                        visited[nextEdge.to] = true;
                        currentVertex = nextEdge.to;
                    }

                    path.push({from: currentVertex, to: vertices[0], weight: 0});
                    totalWeight += path[path.length - 1].weight;

                    if (totalWeight < minWeight) {
                        minWeight = totalWeight;
                        optimalPath = path;
                    }
                }

                return optimalPath;
            }
        });
