            B = [1, 1, 1];
            calculate();
            redraw();
        }

        function calculate() {
            let M = math.matrix([[u[0], v[0]], [u[1], v[1]], [w[0], w[1]]]);
            let U, D, V_transpose;
            [U, D, V_transpose] = math.svd(M);
            let S_hat = U;
            let K = math.multiply(S_hat, math.transpose(S_hat));
            let l_vec = math.matrix([1, 1, 1]);
            let L = math.multiply(math.inv(K), math.multiply(l_vec, math.transpose(l_vec)));
            if (!math.isPositiveDefinite(L)) {
                console.log('Error: L is not positive definite.');
                return;
            }
            let Q = math.cholesky(L);
            let S = math.multiply(U, Q);
            let P = math.multiply(math.inv(Q), math.multiply(D, V_transpose));
            A = math.multiply(math.transpose(math.matrix([1, 1, 1])), math.matrix([A[0], A[1], A[2]]));
            B = math.multiply(math.transpose(math.matrix([1, 1, 1])), math.matrix([B[0], B[1], B[2]]));
        }

        function draw() {
            background(220);
            translate(width / 2, height / 2);
            scale(x_scale, -y_scale);
            stroke(0, 0, 0);
            strokeWeight(3);
            line(0, 0, u[0], u[1]);
            stroke(0, 128, 237);
            strokeWeight(3);
            line(0, 0, v[0], v[1]);
            stroke(0, 0, 205);
            strokeWeight(3);
            line(0, 0, w[0], w[1]);
            fill(255, 0, 0);
            noStroke();
            ellipse(A[0] * x_scale, A[1] * -y_scale, 20);
            fill(255, 165, 0);
            ellipse(B[0] * x_scale, B[1] * -y_scale, 20);
            fill(0, 0, 0);
            textSize(16);
            text('A', A[0] * x_scale + 10, A[1] * -y_scale + 10);
            text('B', B[0] * x_scale + 10, B[1] * -y_scale + 10);
            text('O', 0 + 10, 0 + 10);
            for (let i = 0; i < 3; i++) {
                let proj_u = u[0] * A[i];
                let proj_v = v[0] * A[i];
                let proj_w = w[0] * A[i];
                fill(0, 255, 0);
                ellipse(proj_u * x_scale, proj_v * -y_scale, 10);
                stroke(128, 128, 128);
                strokeWeight(1);
                line(A[0] * x_scale, A[1] * -y_scale, proj_u * x_scale, proj_v * -y_scale);
                line(B[0] * x_scale, B[1] * -y_scale, proj_u * x_scale, proj_v * -y_scale);
                line(0, 0, proj_u * x_scale, proj_v * -y_scale);
            }
        }

        setup();
    </script>
</body>
