            let rect = timePad.getBoundingClientRect();
            let x = e.clientX - rect.left;
            let y = e.clientY - rect.top;

            // Calculate t1 and t2
            let t1 = (x / rect.width) * 2 - 1;
            let t2 = (y / rect.height) * 2 - 1;

            // Update global variables
            this.t1 = t1;
            this.t2 = t2;

