        function updateVisualization() {
            // Clear scene
            scene.clear();

            // Create geometry and material
            let geometry;
            let material;

            if (currentDimension === '2D') {
                geometry = new THREE.BufferGeometry();
                material = new THREE.LineBasicMaterial({ color: 0x4a69bd });

                // Create 2D line
                let positions = new Float32Array(200);
                for (let i = 0; i < 200; i++) {
                    let u = (i / 200) * 6 - 3;
                    let x = u;
                    let y = u * u;
                    positions[i * 3] = x;
                    positions[i * 3 + 1] = y;
                    positions[i * 3 + 2] = 0;
                }
                geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

                // Create line
                let line = new THREE.Line(geometry, material);
                scene.add(line);
            } else {
                // Create 3D geometry and material
                geometry = new THREE.ParametricGeometry((u, v, params) => {
                    let T1 = params[0] * 1.5;
                    let T2 = params[1] * 1.5;

                    // 3A1p
                    if (currentType === '3A1p') {
                        return new THREE.Vector3(u, v, 0.3 * (u * u + v * v) + T1 * u + T2 * v);
                    }
                    // 3A1m
                    else if (currentType === '3A1m') {
                        return new THREE.Vector3(u, v, -0.3 * (u * u + v * v) + T1 * u + T2 * v);
                    }
                    // 3A2
                    else if (currentType === '3A2') {
                        return new THREE.Vector3(2 * u * u * u, 3 * u * u, v);
                    }
                    // 3A3
                    else if (currentType === '3A3') {
                        return new THREE.Vector3(3 * u * u * u * u + u * u * v, -4 * u * u * u * u - 2 * u * v + T1 * 2, v + T2 * 2);
                    }
                    // 3B2p
                    else if (currentType === '3B2p') {
                        return new THREE.Vector3(u, v, (u * u + T1) * (u * u + T1) + v * v + T2);
                    }
                    // 3B2m
                    else if (currentType === '3B2m') {
                        return new THREE.Vector3(u, v, -(u * u * u * u + T1 * 2 * u * u + v * v - T2));
                    }
                    // 3B3
                    else if (currentType === '3B3') {
                        return new THREE.Vector3(u, v, u * u * u + T1 * u + v * v + T2);
                    }
                    // 3C3p
                    else if (currentType === '3C3p') {
                        return new THREE.Vector3(u, v, u * u * u - 3 * u * v * v + T1 * 2 * (u * u + v * v) + T2 * 4 * u);
                    }
                    // 3C3m
                    else if (currentType === '3C3m') {
                        return new THREE.Vector3(u, v, u * u * u + v * v * v + T1 * 4 * u + T2 * 4 * v);
                    }
