                        semiData.push([semiTime, N_d / N_u]);
                        semiTime += dt;
                    }
                }

                for (let i = 0; i < batchData.length; i++) {
                    ratioData.push([batchData[i][0], batchData[i][1] / semiData[i][1]]);
                }

                x = [];
                y = [];
                for (let i = 0; i < ratioData.length; i++) {
                    x.push(ratioData[i][0]);
                    y.push(ratioData[i][1]);
                }
            }

