<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Simulation</title>
    <style>
        #main-container {
            display: flex;
            flex-direction: row;
            height: 100vh;
        }

        #control-panel {
            width: 300px;
            background-color: #f0f0f0;
            padding: 20px;
            border-right: 1px solid #ccc;
        }

        #canvas-container {
            position: relative;
            flex-grow: 1;
            padding: 20px;
        }

        #status-text {
            position: absolute;
            top: 10px;
            left: 50%;
            transform: translateX(-50%);
            font-size: 18px;
            font-weight: bold;
        }

        #btn-reset {
            position: absolute;
            top: 10px;
            right: 20px;
            background-color: #4CAF50;
            color: #fff;
            border: none;
            padding: 10px 20px;
            font-size: 18px;
            cursor: pointer;
        }

        #btn-reset:hover {
            background-color: #3e8e41;
        }

        #bias-value-label {
            position: absolute;
            top: 50px;
            left: 50%;
            transform: translateX(-50%);
        }

        #radius-value-label {
            position: absolute;
            top: 80px;
            left: 50%;
            transform: translateX(-50%);
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Tree type</h4>
            <div id="tree-type-buttons">
                <button id="btn-rt" class="active">Random Tree</button>
                <button id="btn-rrt">RRT</button>
                <button id="btn-rrt-star">RRT*</button>
            </div>
            <h4>Obstacle type</h4>
            <select id="select-obstacle">
                <option value="narrow-passage" selected>narrow passage</option>
                <option value="empty">empty</option>
                <option value="random-rects">random rects</option>
            </select>
            <h4>Number of nodes to add:</h4>
            <div id="add-nodes-buttons">
                <button id="btn-add-1">1</button>
                <button id="btn-add-10">10</button>
                <button id="btn-add-100">100</button>
                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>
            <h4>Exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>
            <h4>Goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>
        <div id="canvas-container">
            <canvas id="canvas"></canvas>
            <p id="status-text"></p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let simulationStarted = false;
        let goalReached = false;
        let explorationBias = 0;
        let goalRadius = 1;
        let treeType = 'Random Tree';
        let obstacleType = 'narrow passage';
        let nodesAdded = 0;
        let pathLength = 0;
        let startNode = { x: 50, y: 250 };
        let goalNode = { x: 450, y: 450 };
        let nodes = [];
        let edges = [];
        let path = [];

        function setup() {
            createCanvas(500, 500);
            background(255);
            frameRate(30);

            // Set up initial state
            nodes.push({ x: startNode.x, y: startNode.y, parent: null });
            nodes[0].color = 'green';
            simulationStarted = true;

            // Add event listeners
            document.getElementById('btn-rt').addEventListener('click', () => {
                treeType = 'Random Tree';
                explorationBias = 0;
                document.getElementById('btn-rt').classList.add('active');
                document.getElementById('btn-rrt').classList.remove('active');
                document.getElementById('btn-rrt-star').classList.remove('active');
                resetSimulation();
            });
            document.getElementById('btn-rrt').addEventListener('click', () => {
                treeType = 'RRT';
                explorationBias = 0;
                document.getElementById('btn-rrt').classList.add('active');
                document.getElementById('btn-rt').classList.remove('active');
                document.getElementById('btn-rrt-star').classList.remove('active');
                resetSimulation();
            });
            document.getElementById('btn-rrt-star').addEventListener('click', () => {
                treeType = 'RRT*';
                explorationBias = 0;
                document.getElementById('btn-rrt-star').classList.add('active');
                document.getElementById('btn-rt').classList.remove('active');
                document.getElementById('btn-rrt').classList.remove('active');
                resetSimulation();
            });
            document.getElementById('select-obstacle').addEventListener('change', () => {
                obstacleType = document.getElementById('select-obstacle').value;
                resetSimulation();
            });
            document.getElementById('slider-bias').addEventListener('input', () => {
                explorationBias = parseFloat(document.getElementById('slider-bias').value);
                document.getElementById('bias-value-label').innerHTML = explorationBias.toFixed(2);
            });
            document.getElementById('slider-radius').addEventListener('input', () => {
                goalRadius = parseFloat(document.getElementById('slider-radius').value);
                document.getElementById('radius-value-label').innerHTML = goalRadius.toFixed(2);
                redrawGoal();
            });
            document.getElementById('btn-reset').addEventListener('click', () => {
                resetSimulation();
            });
            document.getElementById('btn-add-1').addEventListener('click', () => {
                addNode(1);
            });
            document.getElementById('btn-add-10').addEventListener('click', () => {
                addNode(10);
            });
            document.getElementById('btn-add-100').addEventListener('click', () => {
                addNode(100);
            });
            document.getElementById('btn-add-200').addEventListener('click', () => {
                addNode(200);
            });
            document.getElementById('btn-add-500').addEventListener('click', () => {
                addNode(500);
            });
        }

        function draw() {
            background(255);
            drawObstacles();
            drawStartNode();
            drawGoal();
            drawNodes();
            drawEdges();
            drawPath();
            document.getElementById('status-text').innerHTML = `${nodes.length} nodes, ${goalReached ? `path length ${pathLength.toFixed(2)}` : ''}`;
        }

        function addNode(count) {
            for (let i = 0; i < count; i++) {
                if (treeType === 'Random Tree') {
                    addRandomNode();
                } else if (treeType === 'RRT') {
                    addRRTNode();
                } else if (treeType === 'RRT*') {
                    addRRTStarNode();
                }
            }
        }

        function addRandomNode() {
            let newNode = {
                x: random(width),
                y: random(height),
                parent: null,
                color: 'red'
            };
            nodes.push(newNode);
        }

        function addRRTNode() {
            let newNode = {
                x: random(width),
                y: random(height),
                parent: null,
                color: 'red'
            };
            nodes.push(newNode);
            if (random(1) < explorationBias) {
                newNode.parent = goalNode;
            } else {
                let nearestNode = findNearestNode(newNode);
                newNode.parent = nearestNode;
            }
        }

        function addRRTStarNode() {
            let newNode = {
                x: random(width),
                y: random(height),
                parent: null,
                color: 'red'
            };
            nodes.push(newNode);
            if (random(1) < explorationBias) {
                newNode.parent = goalNode;
            } else {
                let nearestNode = findNearestNode(newNode);
                newNode.parent = nearestNode;
            }
            if (goalReached) {
                let newPath = findShortestPath(newNode);
                if (newPath.length < path.length) {
                    path = newPath;
                    pathLength = newPath.length;
                }
            }
        }

        function findNearestNode(node) {
            let nearestNode = null;
            let nearestDistance = Infinity;
            for (let n of nodes) {
                let distance = dist(node.x, node.y, n.x, n.y);
                if (distance < nearestDistance) {
                    nearestNode = n;
                    nearestDistance = distance;
                }
            }
            return nearestNode;
        }

        function findShortestPath(node) {
            let path = [];
            let current = node;
            while (current !== null) {
                path.push(current);
                current = current.parent;
            }
            return path;
        }

        function drawObstacles() {
            if (obstacleType === 'narrow passage') {
                drawNarrowPassage();
            } else if (obstacleType === 'empty') {
                drawEmpty();
            } else if (obstacleType === 'random-rects') {
                drawRandomRects();
            }
        }

        function drawNarrowPassage() {
            fill(0);
            stroke(0);
            strokeWeight(8);
            noFill();
            rect(50, 50, 100, 100);
            rect(200, 50, 100, 100);
        }

        function drawEmpty() {
            // Do nothing
        }

        function drawRandomRects() {
            fill(0);
            stroke(0);
            strokeWeight(8);
            noFill();
            for (let i = 0; i < 10; i++) {
                rect(random(width), random(height), random(50, 100), random(50, 100));
            }
        }

        function drawStartNode() {
            if (simulationStarted) {
                fill(0, 255, 0);
                noStroke();
                circle(startNode.x, startNode.y, 10);
            } else {
                fill(255, 0, 0);
                noStroke();
                circle(startNode.x, startNode.y, 10);
            }
        }

        function drawGoal() {
            fill(255, 255, 0, 100);
            noStroke();
            circle(goalNode.x, goalNode.y, goalRadius * 2);
            fill(0);
            stroke(0);
            strokeWeight(2);
            line(goalNode.x - 5, goalNode.y - 5, goalNode.x + 5, goalNode.y + 5);
            line(goalNode.x + 5, goalNode.y - 5, goalNode.x - 5, goalNode.y + 5);
        }

        function drawNodes() {
            for (let n of nodes) {
                if (n.parent === null) {
                    fill(0, 255, 0);
                } else {
                    fill(255, 0, 0);
                }
                noStroke();
                circle(n.x, n.y, 10);
            }
        }

        function drawEdges() {
            if (treeType === 'RRT*') {
                for (let n of nodes) {
                    if (n.parent !== null) {
                        stroke(0);
                        strokeWeight(0.5);
                        line(n.parent.x, n.parent.y, n.x, n.y);
                    }
                }
            }
        }

        function drawPath() {
            if (goalReached) {
                stroke(0, 255, 0);
                strokeWeight(3);
                for (let i = 0; i < path.length - 1; i++) {
                    line(path[i].x, path[i].y, path[i + 1].x, path[i + 1].y);
                }
            }
        }

        function resetSimulation() {
            simulationStarted = false;
            goalReached = false;
            explorationBias = 0;
            goalRadius = 1;
            treeType = 'Random Tree';
            obstacleType = 'narrow passage';
            nodesAdded = 0;
            pathLength = 0;
            nodes = [];
            edges = [];
            path = [];
            nodes.push({ x: startNode.x, y: startNode.y, parent: null });
            nodes[0].color = 'green';
            simulationStarted = true;
            redrawGoal();
            draw();
        }

        function redrawGoal() {
            background(255);
            drawObstacles();
            drawStartNode();
            drawGoal();
            drawNodes();
            drawEdges();
            drawPath();
        }

        setup();
    </script>
</body>
</html>
