    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        // Initialize MathJax
        const math = document.createElement('script');
        math.id = 'MathJax-script';
        math.async = true;
        math.src = 'https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js';
        document.head.appendChild(math);

        // Initialize three.js scene
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('three-canvas'),
            antialias: true
        });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setSize(window.innerWidth, window.innerHeight);
        camera.position.set(4, 4, 4);
        camera.lookAt(0, 1, 0);
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.target.set(0, 1, 0);
        scene.background = new THREE.Color(0xf0f0f0);
        const ambientLight = new THREE.AmbientLight(0x666666);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
        directionalLight.position.set(5, 5, 5);
        scene.add(directionalLight);
        const axesHelper = new THREE.AxesHelper(4);
        scene.add(axesHelper);

        // Initialize potential surface
        const parametricGeometry = new THREE.ParametricGeometry((u, v) => {
            const rho = 0.4 + u * 2.1;
            const theta = v * 2 * Math.PI;
            const x = rho * Math.cos(theta);
            const y_surf = rho * rho + 1 / (rho * rho);
            const z_surf = -rho * Math.sin(theta);
            return new THREE.Vector3(x, z_surf, y_surf);
        }, 32, 32);
        const material = new THREE.MeshStandardMaterial({
            color: 0xE69943,
            metalness: 0.4,
            roughness: 0.5,
            side: THREE.DoubleSide
        });
        const surfaceMesh = new THREE.Mesh(parametricGeometry, material);
        surfaceMesh.position.set(0, 1.25, 0);
        scene.add(surfaceMesh);

        // Initialize central cylinder
        const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xcccccc,
            metalness: 0.1,
            roughness: 0.9
        });
        const cylinderMesh = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
        cylinderMesh.position.set(0, 1.25, 0);
        scene.add(cylinderMesh);

        // Initialize ion path
        let pathPoints = [];
        const tubeGeometry = new THREE.TubeGeometry(new THREE.CatmullRomCurve3(pathPoints), 512, 0.04, 8);
        const tubeMaterial = new THREE.MeshBasicMaterial({
            color: 0x61AEEF
        });
        const pathMesh = new THREE.Mesh(tubeGeometry, tubeMaterial);
        scene.add(pathMesh);

        // Link sliders to number inputs
        document.getElementById('slider-t').addEventListener('input', () => {
            document.getElementById('input-t').value = document.getElementById('slider-t').value;
            updateVisualization();
        });
        document.getElementById('input-t').addEventListener('input', () => {
            document.getElementById('slider-t').value = document.getElementById('input-t').value;
            updateVisualization();
        });
        document.getElementById('slider-rho0').addEventListener('input', () => {
            document.getElementById('input-rho0').value = document.getElementById('slider-rho0').value;
            updateVisualization();
        });
        document.getElementById('input-rho0').addEventListener('input', () => {
            document.getElementById('slider-rho0').value = document.getElementById('input-rho0').value;
            updateVisualization();
        });
        document.getElementById('slider-zeta0').addEventListener('input', () => {
            document.getElementById('input-zeta0').value = document.getElementById('slider-zeta0').value;
            updateVisualization();
        });
        document.getElementById('input-zeta0').addEventListener('input', () => {
            document.getElementById('slider-zeta0').value = document.getElementById('input-zeta0').value;
            updateVisualization();
        });
        document.getElementById('slider-e0').addEventListener('input', () => {
            document.getElementById('input-e0').value = document.getElementById('slider-e0').value;
            updateVisualization();
        });
        document.getElementById('input-e0').addEventListener('input', () => {
            document.getElementById('slider-e0').value = document.getElementById('input-e0').value;
            updateVisualization();
        });
        document.getElementById('slider-v').addEventListener('input', () => {
            document.getElementById('input-v').value = document.getElementById('slider-v').value;
            updateVisualization();
        });
        document.getElementById('input-v').addEventListener('input', () => {
            document.getElementById('slider-v').value = document.getElementById('input-v').value;
            updateVisualization();
        });
        document.getElementById('slider-lambda').addEventListener('input', () => {
            document.getElementById('input-lambda').value = document.getElementById('slider-lambda').value;
            updateVisualization();
        });
        document.getElementById('input-lambda').addEventListener('input', () => {
            document.getElementById('slider-lambda').value = document.getElementById('input-lambda').value;
            updateVisualization();
        });

        // Perform initial calculation and rendering
        updateVisualization();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Update visualization function
        function updateVisualization() {
            // Read parameters
            const t = parseFloat(document.getElementById('slider-t').value);
            const rho0 = parseFloat(document.getElementById('slider-rho0').value);
            const zeta0 = parseFloat(document.getElementById('slider-zeta0').value);
            const e0 = parseFloat(document.getElementById('slider-e0').value);
            const v = parseFloat(document.getElementById('slider-v').value);
            const lambda = parseFloat(document.getElementById('slider-lambda').value);

            // Path calculation
            const dt = 0.05;
            const nSteps = Math.floor(t / dt);
            const pathPoints = [];
            let rho = rho0;
            let dRhoDs = 0;
            let phi = 0;
            for (let i = 0; i <= nSteps; i++) {
                const s = i * dt;
                const dRhoDsNew = dRhoDs;
                const d2RhoDs2 = -(2 * rho - 2 / rho ** 3) - 4 * lambda ** 2 * rho + 4 * e0 / rho ** 3;
                const dPhiDs = 2 * (e0 / rho ** 2 - lambda);
                const rhoNew = rho + dRhoDs * dt + 0.25 * dt ** 2 * (d2RhoDs2 + 3 * dRhoDsNew * dPhiDs);
                const phiNew = phi + dPhiDs * dt;
                rho = rhoNew;
                dRhoDs = dRhoDsNew + 0.5 * dt * (d2RhoDs2 + dRhoDsNew * dPhiDs);
                phi = phiNew;
                const x = rho * Math.cos(phi);
                const z = rho * Math.sin(phi);
                pathPoints.push(new THREE.Vector3(x, z, -rho));
            }

            // Scene update
            scene.remove(pathMesh);
            const tubeGeometry = new THREE.TubeGeometry(new THREE.CatmullRomCurve3(pathPoints), 512, 0.04, 8);
            const tubeMaterial = new THREE.MeshBasicMaterial({
                color: 0x61AEEF
            });
            pathMesh = new THREE.Mesh(tubeGeometry, tubeMaterial);
            pathMesh.name = 'ion-path';
            scene.add(pathMesh);
        }
    </script>
</body>
</html>
