<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="slider-r">R (Å)</label>
            <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
            <output id="slider-r-value">0.001</output>
        </div>
        <div id="potential-energy-plot-container"></div>
        <div id="density-plots-container">
            <div id="bonding-plot-container"></div>
            <div id="antibonding-plot-container"></div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Generate data for potential energy plot
        const R_values = Array.from({length: 200}, (_, i) => i * 0.1);
        const E_b = R_values.map(R => {
            const S_ab = (1 + R + R**2/3) * Math.exp(-R);
            const J = (1/R) - (1 + 1/R) * Math.exp(-2*R);
            const K = (1 + R) * Math.exp(-R);
            return (J + K) / (1 + S_ab);
        });
        const E_a = R_values.map(R => {
            const S_ab = (1 + R + R**2/3) * Math.exp(-R);
            const J = (1/R) - (1 + 1/R) * Math.exp(-2*R);
            const K = (1 + R) * Math.exp(-R);
            return (J - K) / (1 - S_ab);
        });

        // Create potential energy plot
        const potential_energy_plot = {
            data: [
                {
                    x: R_values.map(R => R * 0.529177),
                    y: E_b,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'bonding PE',
                    line: {color: 'blue'}
                },
                {
                    x: R_values.map(R => R * 0.529177),
                    y: E_a,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'antibonding PE',
                    line: {color: 'red'}
                }
            ],
            layout: {
                title: 'potential energy',
                xaxis: {title: 'R'},
                yaxis: {title: 'Energy (Hartrees)'},
                xaxis: {range: [0, 8]},
                yaxis: {range: [-0.6, 0.4]},
                showlegend: true
            }
        };
        Plotly.plot('potential-energy-plot-container', potential_energy_plot.data, potential_energy_plot.layout);

        // Function to generate electron density data
        function generate_electron_density_data(R) {
            const grid = {
                x: Array.from({length: 60}, (_, i) => i * 0.1 - 3),
                y: Array.from({length: 60}, (_, i) => i * 0.1 - 3)
            };
            const z_bonding_data = grid.x.map(x => {
                return grid.y.map(y => {
                    const dist_A = Math.sqrt((x + R/2)**2 + y**2);
                    const dist_B = Math.sqrt((x - R/2)**2 + y**2);
                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);
                    const S_ab = (1 + R + R**2/3) * Math.exp(-R);
                    const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
                    const psi_b = N_b * (phi_A + phi_B);
                    return psi_b**2;
                });
            });
            const z_antibonding_data = grid.x.map(x => {
                return grid.y.map(y => {
                    const dist_A = Math.sqrt((x + R/2)**2 + y**2);
                    const dist_B = Math.sqrt((x - R/2)**2 + y**2);
                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);
                    const S_ab = (1 + R + R**2/3) * Math.exp(-R);
                    const N_a = 1 / Math.sqrt(2 * (1 - S_ab));
                    const psi_a = N_a * (phi_A - phi_B);
                    return psi_a**2;
                });
            });
            return {z_bonding_data, z_antibonding_data};
        }

        // Update electron density plots on slider change
        document.getElementById('slider-r').addEventListener('input', () => {
            const R_angstroms = parseFloat(document.getElementById('slider-r').value);
            const R_au = R_angstroms / 0.529177;
            const {z_bonding_data, z_antibonding_data} = generate_electron_density_data(R_au);
            const bonding_plot = {
                data: [
                    {
                        z: z_bonding_data,
                        type: 'surface',
                        colorscale: 'Viridis',
                        opacity: 0.8
                    }
                ],
                layout: {
                    scene: {
                        xaxis: {range: [-3, 3]},
                        yaxis: {range: [-3, 3]},
                        zaxis: {range: [0, 0.5]},
                        showbackground: false,
                        showgrid: false,
                        showaxes: false,
                        camera: {
                            eye: {x: 1.5, y: 1.5, z: 1.5}
                        }
                    },
                    showlegend: false
                }
            };
            Plotly.restyle('bonding-plot-container', bonding_plot.data, bonding_plot.layout);

            const antibonding_plot = {
                data: [
                    {
                        z: z_antibonding_data,
                        type: 'surface',
                        colorscale: 'Viridis',
                        opacity: 0.8
                    }
                ],
                layout: {
                    scene: {
                        xaxis: {range: [-3, 3]},
                        yaxis: {range: [-3, 3]},
                        zaxis: {range: [0, 0.5]},
                        showbackground: false,
                        showgrid: false,
                        showaxes: false,
                        camera: {
                            eye: {x: 1.5, y: 1.5, z: 1.5}
                        }
                    },
                    showlegend: false
                }
            };
            Plotly.restyle('antibonding-plot-container', antibonding_plot.data, antibonding_plot.layout);

            document.getElementById('slider-r-value').value = R_angstroms.toFixed(3);
        });
    </script>
</body>
</html>
