    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let time = 0;
        let animationRunning = false;
        let rotationMode = 'normal';
        let dayNight = false;
        let nearSide = true;
        let earthMoonLine = true;
        let moonOnly = false;

        document.getElementById('slider-time').addEventListener('input', () => {
            time = parseInt(document.getElementById('slider-time').value);
            updateDisplayTime();
            draw();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            animationRunning = !animationRunning;
            if (animationRunning) {
                document.getElementById('btn-play-pause').textContent = '❚❚';
                animate();
            } else {
                document.getElementById('btn-play-pause').textContent = '▶';
                clearInterval(animationInterval);
            }
        });

        document.getElementById('btn-step-back').addEventListener('click', () => {
            time -= 1;
            updateSlider();
            draw();
        });

        document.getElementById('btn-step-forward').addEventListener('click', () => {
            time += 1;
            updateSlider();
            draw();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            time = 0;
            document.getElementById('slider-time').value = 0;
            draw();
        });

        document.getElementById('btn-rot-half').addEventListener('click', () => {
            rotationMode = 'half';
            updateRotationMode();
        });

        document.getElementById('btn-rot-normal').addEventListener('click', () => {
            rotationMode = 'normal';
            updateRotationMode();
        });

        document.getElementById('btn-rot-double').addEventListener('click', () => {
            rotationMode = 'double';
            updateRotationMode();
        });

        document.getElementById('btn-rot-none').addEventListener('click', () => {
            rotationMode = 'none';
            updateRotationMode();
        });

        document.getElementById('check-day-night').addEventListener('change', () => {
            dayNight = document.getElementById('check-day-night').checked;
            draw();
        });

        document.getElementById('check-near-side').addEventListener('change', () => {
            nearSide = document.getElementById('check-near-side').checked;
            draw();
        });

        document.getElementById('check-earth-moon-line').addEventListener('change', () => {
            earthMoonLine = document.getElementById('check-earth-moon-line').checked;
            draw();
        });

        document.getElementById('check-moon-only').addEventListener('change', () => {
            moonOnly = document.getElementById('check-moon-only').checked;
            draw();
        });

        function updateDisplayTime() {
            let days = Math.floor(time / 24);
            let hours = time % 24;
            document.getElementById('display-time').textContent = `${days} d ${hours} h`;
        }

        function updateSlider() {
            document.getElementById('slider-time').value = time;
        }

        function updateRotationMode() {
            let activeButton = document.querySelector('#rotation-selector button.active');
            activeButton.classList.remove('active');
            document.getElementById(`btn-${rotationMode}`).classList.add('active');
            draw();
        }

        function draw() {
            background(220);
            translate(width / 2, height / 2);
            if (!moonOnly) {
                drawEarth();
                push();
                rotate(earthRotation);
                drawRedStickFigure();
                pop();
                if (dayNight) {
                    drawDayNightTerminator();
                }
            }
            drawMoon();
            if (earthMoonLine && !moonOnly) {
                drawEarthMoonLine();
            }
