            vertex(-20, 0);
            vertex(0, 0);
            vertex(20, 0);
            vertex(40, 20);
            vertex(60, 0);
            vertex(80, 20);
            endShape(CLOSE);
            push();
            rotate(earthRotation);
            drawRedStickFigure();
            pop();
