                drawDayNightTerminator();
            }
        }

        function drawEarthMoonLine() {
            stroke(0);
            strokeDasharray(10, 10);
            line(0, 0, mx, my);
        }

        let earthRotation = 0;
        let moonOrbit = 0;
        let moonRotation = 0;
        let mx = 0;
        let my = 0;

        function setup() {
            createCanvas(800, 600);
            background(220);
            noStroke();
            frameRate(60);
