        input[type="radio"]:checked + label::before {
            background-color: #4CAF50;
        }

        input[type="radio"] + label span {
            display: inline-block;
            vertical-align: middle;
        }

        input[type="range"] {
            width: 100%;
        }

        .slider-value {
            display: inline-block;
            margin-left: 10px;
        }
    </style>
</head>
<body>
    <h1>Rovibronic Spectrum of a Perpendicular Band of a Symmetric Rotor</h1>

    <!-- Control Panel -->
    <div id="control-panel" class="card">
        <!-- View Selector -->
        <div id="view-selector-group">
            <label>select view</label>
            <input type="radio" name="view-selector" id="radio-view-full" value="full" checked class="toggle-button checked">
            <label for="radio-view-full">full spectrum</label>
            <input type="radio" name="view-selector" id="radio-view-sub" value="sub" class="toggle-button">
            <label for="radio-view-sub">sub-band</label>
        </div>

        <!-- Sub-band K Selector -->
        <div id="k-selector-group" class="disabled">
            <label>select sub-band K =</label>
            <input type="radio" name="k-selector" id="radio-k-0" value="0" checked class="toggle-button checked">
            <label for="radio-k-0">0</label>
            <input type="radio" name="k-selector" id="radio-k-1" value="1" class="toggle-button">
            <label for="radio-k-1">1</label>
            <input type="radio" name="k-selector" id="radio-k-2" value="2" class="toggle-button">
            <label for="radio-k-2">2</label>
            <input type="radio" name="k-selector" id="radio-k-3" value="3" class="toggle-button">
            <label for="radio-k-3">3</label>
            <input type="radio" name="k-selector" id="radio-k-4" value="4" class="toggle-button">
            <label for="radio-k-4">4</label>
            <input type="radio" name="k-selector" id="radio-k-5" value="5" class="toggle-button">
            <label for="radio-k-5">5</label>
        </div>

        <!-- Sub-band Type Selector -->
        <div id="subband-type-group" class="disabled">
            <label>select +/- sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-full" value="full" checked class="toggle-button checked">
            <label for="radio-subband-full">full sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-positive" value="positive" class="toggle-button">
            <label for="radio-subband-positive">positive sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-negative" value="negative" class="toggle-button">
            <label for="radio-subband-negative">negative sub-band</label>
        </div>

        <!-- Branch Selector -->
        <div id="branch-selector-group" class="disabled">
            <label>select branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-all" value="all" checked class="toggle-button checked">
            <label for="radio-branch-all">all branches</label>
            <input type="radio" name="branch-selector" id="radio-branch-p" value="P" class="toggle-button">
            <label for="radio-branch-p">P branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-q" value="Q" class="toggle-button">
            <label for="radio-branch-q">Q branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-r" value="R" class="toggle-button">
            <label for="radio-branch-r">R branch</label>
        </div>
    </div>

    <!-- Visualization Area -->
    <div id="plot-div" class="plot-div"></div>

    <!-- Zoom Control Panel -->
    <div id="zoom-controls" class="card">
        <div>
            <label for="slider-x-lower">x axis lower boundary</label>
            <input type="range" id="slider-x-lower" min="900" max="1100" step="1" value="902">
            <span id="span-x-lower-value" class="slider-value">902.</span>
        </div>
        <div>
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" step="1" value="1091">
            <span id="span-x-upper-value" class="slider-value">1091.</span>
        </div>
    </div>

    <script>
        // Initialize plot
        let plot = {
            data: [],
            layout: {
                title: '',
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [902, 1091]
                },
                yaxis: {
                    title: 'absorbance',
                    range: [0, 1.2]
                },
                showlegend: false,
                bargap: 0
            }
        };

        // Update plot
        function updatePlot() {
            // Clear existing data
            plot.data = [];

            // Calculate data based on user selections
            let k = parseInt(document.querySelector('#radio-k-0').checked ? '0' : document.querySelector('#radio-k-1').checked ? '1' : document.querySelector('#radio-k-2').checked ? '2' : document.querySelector('#radio-k-3').checked ? '3' : document.querySelector('#radio-k-4').checked ? '4' : '5');
            let subbandType = document.querySelector('#radio-subband-full').checked ? 'full' : document.querySelector('#radio-subband-positive').checked ? 'positive' : 'negative';
            let branch = document.querySelector('#radio-branch-all').checked ? 'all' : document.querySelector('#radio-branch-p').checked ? 'P' : document.querySelector('#radio-branch-q').checked ? 'Q' : 'R';

            // Calculate data for full spectrum
            if (subbandType === 'full') {
                for (let J = 0; J <= 60; J++) {
                    let wavenumber = 1000 + J * 0.98 - k * 0.2;
                    let intensity = Math.exp(-J * 0.2 / 300) * (1 + Math.exp(-J * 0.2 / 300));
                    plot.data.push({
                        x: [wavenumber],
                        y: [intensity],
                        mode: 'markers',
                        type: 'scatter',
                        marker: {
                            color: 'black'
                        }
                    });
                }
            }
            // Calculate data for positive sub-band
            else if (subbandType === 'positive') {
                for (let J = 0; J <= 60; J++) {
                    let wavenumber = 1000 + J * 0.98 + k * 0.2;
                    let intensity = Math.exp(-J * 0.2 / 300) * (1 + Math.exp(-J * 0.2 / 300));
                    plot.data.push({
                        x: [wavenumber],
                        y: [intensity],
                        mode: 'markers',
                        type: 'scatter',
                        marker: {
                            color: 'red'
                        }
                    });
                }
            }
            // Calculate data for negative sub-band
            else {
                for (let J = 0; J <= 60; J++) {
                    let wavenumber = 1000 + J * 0.98 - k * 0.2;
                    let intensity = Math.exp(-J * 0.2 / 300) * (1 + Math.exp(-J * 0.2 / 300));
                    plot.data.push({
                        x: [wavenumber],
                        y: [intensity],
                        mode: 'markers',
                        type: 'scatter',
                        marker: {
                            color: 'blue'
                        }
                    });
                }
            }

            // Update plot title
            if (subbandType === 'full') {
                plot.layout.title = 'Rovibronic Spectrum';
            } else {
                plot.layout.title = 'K = ' + k + ' sub-band\n"' + subbandType + '" sub-band';
            }

            // Update plot
            Plotly.newPlot('plot-div', plot.data, plot.layout);
        }

        // Update plot on user selections
        document.querySelector('#radio-view-full').addEventListener('change', function() {
            document.querySelector('#k-selector-group').classList.toggle('disabled');
            document.querySelector('#subband-type-group').classList.toggle('disabled');
            document.querySelector('#branch-selector-group').classList.toggle('disabled');
            document.querySelector('#branch-selector-group').classList.remove('active');
            updatePlot();
        });

        document.querySelector('#radio-view-sub').addEventListener('change', function() {
            document.querySelector('#k-selector-group').classList.remove('disabled');
            document.querySelector('#subband-type-group').classList.remove('disabled');
            document.querySelector('#branch-selector-group').classList.remove('disabled');
            document.querySelector('#branch-selector-group').classList.add('active');
            updatePlot();
        });

        document.querySelector('#radio-k-0').addEventListener('change', updatePlot);
        document.querySelector('#radio-k-1').addEventListener('change', updatePlot);
        document.querySelector('#radio-k-2').addEventListener('change', updatePlot);
        document.querySelector('#radio-k-3').addEventListener('change', updatePlot);
        document.querySelector('#radio-k-4').addEventListener('change', updatePlot);
        document.querySelector('#radio-k-5').addEventListener('change', updatePlot);

        document.querySelector('#radio-subband-full').addEventListener('change', function() {
            document.querySelector('#branch-selector-group').classList.remove('active');
            updatePlot();
        });

        document.querySelector('#radio-subband-positive').addEventListener('change', function() {
            document.querySelector('#branch-selector-group').classList.add('active');
            updatePlot();
        });

        document.querySelector('#radio-subband-negative').addEventListener('change', function() {
            document.querySelector('#branch-selector-group').classList.add('active');
            updatePlot();
        });

        document.querySelector('#radio-branch-all').addEventListener('change', updatePlot);
        document.querySelector('#radio-branch-p').addEventListener('change', updatePlot);
        document.querySelector('#radio-branch-q').addEventListener('change', updatePlot);
        document.querySelector('#radio-branch-r').addEventListener('change', updatePlot);

        // Update plot on slider changes
        document.querySelector('#slider-x-lower').addEventListener('input', function() {
            let value = parseInt(this.value);
            document.querySelector('#span-x-lower-value').textContent = value + '.';
            plot.layout.xaxis.range = [value, plot.layout.xaxis.range[1]];
            Plotly.relayout('plot-div', plot.layout);
        });

        document.querySelector('#slider-x-upper').addEventListener('input', function() {
            let value = parseInt(this.value);
            document.querySelector('#span-x-upper-value').textContent = value + '.';
            plot.layout.xaxis.range = [plot.layout.xaxis.range[0], value];
            Plotly.relayout('plot-div', plot.layout);
        });

        // Initialize plot
        updatePlot();
    </script>
</body>
</html>
