    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-strike">strike angle \(\phi\) of fault plane clockwise from North (0° ≤ \(\phi\) < 360°)</label>
                <input type="range" id="slider-strike" min="0" max="360" step="1" value="180">
                <span id="strike-value">180</span>
            </div>
            <div class="slider-container">
                <label for="slider-dip">dip angle \(\delta\) of fault plane down from horizontal (0° ≤ \(\delta\) ≤ 90°)</label>
                <input type="range" id="slider-dip" min="0" max="90" step="1" value="60">
                <span id="dip-value">60</span>
            </div>
            <div class="slider-container">
                <label for="slider-rake">slip angle \(\lambda\) of fault plane between slip direction and strike (−180° ≤ \(\lambda\) < 180°)</label>
                <input type="range" id="slider-rake" min="-180" max="180" step="1" value="90">
                <span id="rake-value">90</span>
            </div>
            <div class="button-container">
                <label>wave type</label>
                <button id="btn-p" class="active">P</button>
                <button id="btn-sv">SV</button>
                <button id="btn-sh">SH</button>
            </div>
        </div>
        <div id="visualization-panel">
            <div id="canvas-container">
                <!-- three.js canvas will be inserted here by JavaScript -->
            </div>
            <div id="overlay-controls">
                <label for="checkbox-box">box</label>
                <input type="checkbox" id="checkbox-box">
            </div>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.163.0/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.163.0/examples/jsm/"
            }
        }
    </script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.163.0/examples/jsm/OrbitControls.js"></script>
    <script>
        // Initialize variables
        let currentWaveType = 'P';
        let strikeValue = 180;
        let dipValue = 60;
        let rakeValue = 90;
        let checkboxBox = document.getElementById('checkbox-box');
        let canvasContainer = document.getElementById('canvas-container');
        let strikeSlider = document.getElementById('slider-strike');
        let dipSlider = document.getElementById('slider-dip');
        let rakeSlider = document.getElementById('slider-rake');
        let btnP = document.getElementById('btn-p');
        let btnSV = document.getElementById('btn-sv');
        let btnSH = document.getElementById('btn-sh');
        let strikeValueDisplay = document.getElementById('strike-value');
        let dipValueDisplay = document.getElementById('dip-value');
        let rakeValueDisplay = document.getElementById('rake-value');

        // Add event listeners
        strikeSlider.addEventListener('input', updateStrikeValue);
        dipSlider.addEventListener('input', updateDipValue);
        rakeSlider.addEventListener('input', updateRakeValue);
        btnP.addEventListener('click', selectWaveType);
        btnSV.addEventListener('click', selectWaveType);
        btnSH.addEventListener('click', selectWaveType);
        checkboxBox.addEventListener('change', toggleBoundingBox);

        // Initialize three.js scene
        let scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);
        let camera = new THREE.PerspectiveCamera(75, canvasContainer.offsetWidth / canvasContainer.offsetHeight, 0.1, 1000);
        let renderer = new THREE.WebGLRenderer({
            canvas: document.createElement('canvas'),
            antialias: true
        });
        renderer.setSize(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
        canvasContainer.appendChild(renderer.domElement);
        let controls = new THREE.OrbitControls(camera, renderer.domElement);
        let ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        let directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);
        let directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Initialize radiation pattern mesh
        let parametricGeometry = new THREE.ParametricGeometry((u, v) => {
            let r = Math.abs(Math.sin(2 * v) * Math.sin(u));
            return new THREE.Vector3(r * Math.cos(u), r * Math.sin(u), 0);
        }, 100, 100);
        let meshStandardMaterial = new THREE.MeshStandardMaterial({
            color: 0xd2b48c,
            side: THREE.DoubleSide
        });
        let radiationPatternMesh = new THREE.Mesh(parametricGeometry, meshStandardMaterial);
        scene.add(radiationPatternMesh);

        // Initialize compass overlay
        let compassOverlay = new THREE.Group();
        scene.add(compassOverlay);
        let outerCircleGeometry = new THREE.CircleGeometry(10, 32);
        let outerCircleMaterial = new THREE.LineDashedMaterial({
            color: 0xffffff,
            dashSize: 2,
            gapSize: 2
        });
        let outerCircle = new THREE.LineLoop(outerCircleGeometry, outerCircleMaterial);
        compassOverlay.add(outerCircle);
        let cardinalDirectionSprites = [];
        let degreeMarkSprites = [];
        for (let i = 0; i < 12; i++) {
            let angle = i * Math.PI / 6;
            let position = new THREE.Vector3(Math.cos(angle), Math.sin(angle), 0);
            let cardinalDirectionSprite = new THREE.Sprite(new THREE.SpriteMaterial({
                map: new THREE.CanvasTexture(getCardinalDirectionText(angle))
            }));
            cardinalDirectionSprites.push(cardinalDirectionSprite);
            cardinalDirectionSprite.position.copy(position);
            cardinalDirectionSprite.scale.set(2, 2, 2);
            compassOverlay.add(cardinalDirectionSprite);
            let degreeMarkSprite = new THREE.Sprite(new THREE.SpriteMaterial({
                map: new THREE.CanvasTexture(getDegreeMarkText(angle))
            }));
            degreeMarkSprites.push(degreeMarkSprite);
            degreeMarkSprite.position.copy(position);
            degreeMarkSprite.scale.set(1, 1, 1);
            compassOverlay.add(degreeMarkSprite);
        }
        let uArrowHelper = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 10, 0x000000);
        compassOverlay.add(uArrowHelper);
        let uTextSprite = new THREE.Sprite(new THREE.SpriteMaterial({
            map: new THREE.CanvasTexture(getUText())
        }));
        compassOverlay.add(uTextSprite);

        // Initialize bounding box
        let boundingBoxHelper = new THREE.BoxHelper(radiationPatternMesh, 0xffffff);
        scene.add(boundingBoxHelper);

        // Render the scene
        function render() {
            controls.update();
            renderer.render(scene, camera);
            requestAnimationFrame(render);
        }
        render();

        // Update the scene based on user input
        function updateStrikeValue() {
            strikeValue = parseInt(strikeSlider.value);
            strikeValueDisplay.textContent = strikeValue;
            updateRadiationPatternMesh();
        }
        function updateDipValue() {
            dipValue = parseInt(dipSlider.value);
            dipValueDisplay.textContent = dipValue;
            updateRadiationPatternMesh();
        }
        function updateRakeValue() {
            rakeValue = parseInt(rakeSlider.value);
            rakeValueDisplay.textContent = rakeValue;
            updateRadiationPatternMesh();
        }
        function selectWaveType() {
            if (this === btnP) {
                currentWaveType = 'P';
                btnP.classList.add('active');
                btnSV.classList.remove('active');
                btnSH.classList.remove('active');
            } else if (this === btnSV) {
                currentWaveType = 'SV';
                btnSV.classList.add('active');
                btnP.classList.remove('active');
                btnSH.classList.remove('active');
            } else if (this === btnSH) {
                currentWaveType = 'SH';
                btnSH.classList.add('active');
                btnP.classList.remove('active');
                btnSV.classList.remove('active');
            }
            updateRadiationPatternMesh();
        }
        function toggleBoundingBox() {
            boundingBoxHelper.visible = this.checked;
        }

        // Update the radiation pattern mesh
        function updateRadiationPatternMesh() {
            let parametricGeometry = new THREE.ParametricGeometry((u, v) => {
                let r;
                if (currentWaveType === 'P') {
                    r = Math.abs(Math.sin(2 * v) * Math.sin(u));
                } else if (currentWaveType === 'SV') {
                    r = Math.abs(Math.cos(2 * v) * Math.sin(u));
                } else if (currentWaveType === 'SH') {
                    r = Math.abs(Math.cos(v) * Math.cos(u));
                }
                return new THREE.Vector3(r * Math.cos(u), r * Math.sin(u), 0);
            }, 100, 100);
            let meshStandardMaterial = new THREE.MeshStandardMaterial({
                color: 0xd2b48c,
                side: THREE.DoubleSide
            });
            let radiationPatternMesh = new THREE.Mesh(parametricGeometry, meshStandardMaterial);
            radiationPatternMesh.position.x = strikeValue * Math.PI / 180;
            radiationPatternMesh.position.y = dipValue * Math.PI / 180;
            radiationPatternMesh.position.z = rakeValue * Math.PI / 180;
            radiationPatternMesh.rotation.x = strikeValue * Math.PI / 180;
            radiationPatternMesh.rotation.y = dipValue * Math.PI / 180;
            radiationPatternMesh.rotation.z = rakeValue * Math.PI / 180;
            scene.remove(radiationPatternMesh);
            scene.add(radiationPatternMesh);
            updateBoundingBoxHelper();
        }

        // Update the bounding box helper
        function updateBoundingBoxHelper() {
            boundingBoxHelper.geometry = new THREE.BoxGeometry(radiationPatternMesh.scale.x, radiationPatternMesh.scale.y, radiationPatternMesh.scale.z);
            boundingBoxHelper.update();
        }

        // Get the cardinal direction text
        function getCardinalDirectionText(angle) {
            let canvas = document.createElement('canvas');
            canvas.width = 100;
            canvas.height = 100;
            let context = canvas.getContext('2d');
            context.font = '24px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            if (angle >= 0 && angle < Math.PI / 2) {
                context.fillStyle = 0xffffff;
                context.fillText('N', canvas.width / 2, canvas.height / 2);
            } else if (angle >= Math.PI / 2 && angle < Math.PI) {
                context.fillStyle = 0xffffff;
                context.fillText('E', canvas.width / 2, canvas.height / 2);
            } else if (angle >= Math.PI && angle < 3 * Math.PI / 2) {
                context.fillStyle = 0xffffff;
                context.fillText('S', canvas.width / 2, canvas.height / 2);
            } else if (angle >= 3 * Math.PI / 2 && angle < 2 * Math.PI) {
                context.fillStyle = 0xffffff;
                context.fillText('W', canvas.width / 2, canvas.height / 2);
            }
            return canvas;
        }

        // Get the degree mark text
        function getDegreeMarkText(angle) {
            let canvas = document.createElement('canvas');
            canvas.width = 100;
            canvas.height = 100;
            let context = canvas.getContext('2d');
            context.font = '18px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillStyle = 0xffffff;
            context.fillText(Math.floor(angle * 180 / Math.PI), canvas.width / 2, canvas.height / 2);
            return canvas;
        }

        // Get the U text
        function getUText() {
            let canvas = document.createElement('canvas');
            canvas.width = 100;
            canvas.height = 100;
            let context = canvas.getContext('2d');
            context.font = '24px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillStyle = 0xffffff;
            context.fillText('U', canvas.width / 2, canvas.height / 2);
            return canvas;
        }
    </script>
</body>
</html>
