<body>
    <main>
        <section class="control-panel">
            <h2>Control Panel</h2>
            <div>
                <label for="slider-terms">Number of Terms:</label>
                <input type="range" id="slider-terms" min="2" max="50" step="1" value="10">
                <span id="span-terms-value">10</span>
            </div>
