                <label for="radio-euler-0">Repeated Euler Transformations:</label>
                <input type="radio" id="radio-euler-0" name="euler-transformations" value="0" checked>
                <label for="radio-euler-0">0</label>
                <input type="radio" id="radio-euler-1" name="euler-transformations" value="1">
                <label for="radio-euler-1">1</label>
                <input type="radio" id="radio-euler-2" name="euler-transformations" value="2">
                <label for="radio-euler-2">2</label>
                <input type="radio" id="radio-euler-3" name="euler-transformations" value="3">
                <label for="radio-euler-3">3</label>
            </div>
            <div>
                <label for="radio-series-pi">Limit of Infinite Sequence:</label>
                <input type="radio" id="radio-series-pi" name="series-select" value="pi" checked>
                <label for="radio-series-pi">π</label>
                <input type="radio" id="radio-series-ln2" name="series-select" value="ln2">
                <label for="radio-series-ln2">ln(2)</label>
                <input type="radio" id="radio-series-sqrt2" name="series-select" value="sqrt2">
                <label for="radio-series-sqrt2">√2</label>
            </div>
        </section>
        <section class="visualization-area">
            <h2>Visualization Area</h2>
            <canvas id="canvas-plot" width="800" height="600"></canvas>
        </section>
        <section class="formula-display">
            <h2>Formula Display</h2>
            <div id="div-formula"></div>
        </section>
    </main>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        let nTerms = 10;
        let eulerTransformations = 0;
        let seriesType = "pi";
        let seriesData = [];
        let transformedSeriesData = [];
        let canvas;
        let ctx;

        function setup() {
            canvas = createCanvas(800, 600);
            ctx = canvas.getContext('2d');
            background(220);
            noLoop();
        }

        function draw() {
            background(220);
            drawAxes();
            drawSeriesLines();
        }

        function drawAxes() {
            ctx.save();
            ctx.translate(50, height / 2);
            ctx.rotate(-PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("N", 0, -10);
            ctx.text("S_N", 0, 10);
            ctx.restore();
            ctx.save();
            ctx.translate(width / 2, 50);
            ctx.rotate(PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("0", 0, 10);
            ctx.text("1", 0, -10);
            ctx.restore();
        }

        function drawSeriesLines() {
            ctx.save();
            ctx.translate(50, height / 2);
            ctx.rotate(-PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("N", 0, -10);
            ctx.text("S_N", 0, 10);
            ctx.restore();
            ctx.save();
            ctx.translate(width / 2, 50);
            ctx.rotate(PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("0", 0, 10);
            ctx.text("1", 0, -10);
            ctx.restore();
            ctx.save();
            ctx.translate(50, height / 2);
            ctx.rotate(-PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("N", 0, -10);
            ctx.text("S_N", 0, 10);
            ctx.restore();
            ctx.save();
            ctx.translate(width / 2, 50);
            ctx.rotate(PI / 2);
            ctx.textAlign(CENTER, CENTER);
            ctx.textFont("Arial");
            ctx.textSize(12);
            ctx.fillStyle = "#ccc";
            ctx.text("0", 0, 10);
            ctx.text("1", 0, -10);
            ctx.restore();
            for (let i = 0; i < seriesData.length; i++) {
                ctx.beginPath();
                ctx.strokeStyle = seriesData[i].color;
                ctx.lineWidth = 2;
                ctx.moveTo(50 + i * 10, height / 2 - seriesData[i].y);
                ctx.lineTo(50 + (i + 1) * 10, height / 2 - seriesData[i + 1].y);
                ctx.stroke();
            }
            if (eulerTransformations > 0) {
                for (let i = 0; i < transformedSeriesData.length; i++) {
                    ctx.beginPath();
                    ctx.strokeStyle = transformedSeriesData[i].color;
                    ctx.lineWidth = 2;
                    ctx.moveTo(50 + i * 10 + (eulerTransformations - 1) * 5, height / 2 - transformedSeriesData[i].y);
                    ctx.lineTo(50 + (i + 1) * 10 + (eulerTransformations - 1) * 5, height / 2 - transformedSeriesData[i + 1].y);
                    ctx.stroke();
                }
            }
        }

        function updateVisualization() {
            seriesData = [];
            transformedSeriesData = [];
            for (let i = 0; i < nTerms; i++) {
                let partialSum = 0;
                if (seriesType === "pi") {
                    partialSum = 4 / (2 * i + 1) * Math.pow(-1, i);
                } else if (seriesType === "ln2") {
                    partialSum = 1 / i * Math.pow(-1, i);
                } else if (seriesType === "sqrt2") {
                    let a0 = 1;
                    for (let j = 0; j < i; j++) {
                        a0 = a0 * (1 / 2 - j) / (j + 1);
                    }
                    partialSum = a0;
                }
                seriesData.push({ x: 50 + i * 10, y: height / 2 - partialSum * 100 });
            }
            for (let i = 0; i < nTerms - 1; i++) {
                let transformedPartialSum = (seriesData[i].y + seriesData[i + 1].y) / 2;
                transformedSeriesData.push({ x: 50 + i * 10 + (eulerTransformations - 1) * 5, y: height / 2 - transformedPartialSum * 100 });
            }
            redraw();
        }

        function updateSeriesType() {
            seriesType = document.querySelector('input[name="series-select"]:checked').value;
            updateVisualization();
        }

        function updateEulerTransformations() {
            eulerTransformations = document.querySelector('input[name="euler-transformations"]:checked').value;
            updateVisualization();
        }

        function updateNumberOfTerms() {
            nTerms = parseInt(document.getElementById("slider-terms").value);
            updateVisualization();
        }

        document.getElementById("slider-terms").addEventListener("input", updateNumberOfTerms);
        document.getElementById("radio-euler-0").addEventListener("change", updateEulerTransformations);
        document.getElementById("radio-euler-1").addEventListener("change", updateEulerTransformations);
        document.getElementById("radio-euler-2").addEventListener("change", updateEulerTransformations);
        document.getElementById("radio-euler-3").addEventListener("change", updateEulerTransformations);
        document.querySelectorAll('input[name="series-select"]').forEach((radio) => {
            radio.addEventListener("change", updateSeriesType);
        });

        setup();
        updateVisualization();
        MathJax.typeset();
    </script>
</body>
</html>
