    <script>
        let share1_graphic, share2_graphic;
        let x1 = 50, y1 = 50;
        let x2 = 50, y2 = 50;

        function setup() {
            createCanvas(800, 600);
            background(255);

            share1_graphic = createGraphics(128, 128);
            share2_graphic = createGraphics(128, 128);

            let secret_image = [];
            for (let i = 0; i < 64; i++) {
                secret_image[i] = [];
                for (let j = 0; j < 64; j++) {
                    secret_image[i][j] = floor(random(2));
                }
            }

            for (let i = 0; i < 64; i++) {
                for (let j = 0; j < 64; j++) {
                    if (secret_image[i][j] === 0) {
                        let pattern1 = [[color(0), color(255, 0, 0, 0)], [color(255, 0, 0, 0), color(0)]];
                        let pattern2 = [[color(255, 0, 0, 0), color(0)], [color(0), color(255, 0, 0, 0)]];
                        let pattern = random([pattern1, pattern2]);
                        share1_graphic.fill(pattern[0][0]);
                        share1_graphic.noStroke();
                        share1_graphic.rect(i*2, j*2, 2, 2);
                        share2_graphic.fill(pattern[1][0]);
                        share2_graphic.noStroke();
                        share2_graphic.rect(i*2, j*2, 2, 2);
                    } else {
                        let pattern1 = [[color(0), color(255, 0, 0, 0)], [color(255, 0, 0, 0), color(0)]];
                        let pattern2 = [[color(255, 0, 0, 0), color(0)], [color(0), color(255, 0, 0, 0)]];
                        let pattern = random([pattern1, pattern2]);
                        share1_graphic.fill(pattern[0][0]);
                        share1_graphic.noStroke();
                        share1_graphic.rect(i*2, j*2, 2, 2);
                        share2_graphic.fill(pattern[1][1]);
                        share2_graphic.noStroke();
                        share2_graphic.rect(i*2, j*2, 2, 2);
                    }
                }
            }
        }

        function draw() {
            background(255);
            image(share1_graphic, x1, y1);
            image(share2_graphic, x2, y2);
        }

        function mousePressed() {
            if (mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
                if (dist(mouseX, mouseY, x1 + 50, y1 + 50) < 10) {
                    x1 = mouseX;
                    y1 = mouseY;
                } else if (dist(mouseX, mouseY, x2 + 50, y2 + 50) < 10) {
                    x2 = mouseX;
                    y2 = mouseY;
                }
            }
        }

        function mouseDragged() {
            if (mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
                if (dist(mouseX, mouseY, x1 + 50, y1 + 50) < 10) {
                    x1 = mouseX;
                    y1 = mouseY;
                } else if (dist(mouseX, mouseY, x2 + 50, y2 + 50) < 10) {
                    x2 = mouseX;
                    y2 = mouseY;
                }
            }
