        function mouseReleased() {
            if (mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
                if (dist(mouseX, mouseY, x1 + 50, y1 + 50) < 10) {
                    x1 = mouseX;
                    y1 = mouseY;
                } else if (dist(mouseX, mouseY, x2 + 50, y2 + 50) < 10) {
                    x2 = mouseX;
                    y2 = mouseY;
                }
            }
        }

        document.getElementById('joystick-handle1').addEventListener('mousedown', function() {
            document.addEventListener('mousemove', mouseDragged);
            document.addEventListener('mouseup', function() {
                document.removeEventListener('mousemove', mouseDragged);
            });
        });

        document.getElementById('joystick-handle2').addEventListener('mousedown', function() {
            document.addEventListener('mousemove', mouseDragged);
            document.addEventListener('mouseup', function() {
                document.removeEventListener('mousemove', mouseDragged);
            });
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            x1 = 50;
            y1 = 50;
            x2 = 50;
            y2 = 50;
        });
    </script>
