        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="rings-control-group">
                <label for="rings-radios">rings</label>
                <div>
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>
            <div id="circles-per-ring-control-group">
                <label for="cpr-radios">circles per ring</label>
                <div>
                    <input type="radio" name="cpr" value="4" id="cpr-4" checked>
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8">
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas" width="550" height="550"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let locators = [new p5.Vector(-50, 0), new p5.Vector(50, 0)];
        let draggedLocator = null;

        function setup() {
            createCanvas(550, 550);
            angleMode(RADIANS);
            background(220);
            translate(width / 2, height / 2);
        }

        function draw() {
            background(135, 213, 230); // #87d4e6
            drawNormalMode();
            if (document.getElementById("checkbox-inverse").checked) {
                drawInverseMode();
            }
        }

        function drawNormalMode() {
            let N = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            let M = parseInt(document.querySelector('input[name="rings"]:checked').value);
            let R_max = width / 2 * 0.9;
            let k = (1 - sin(PI / N)) / (1 + sin(PI / N));
            let R_c = R_max / (1 + sin(PI / N));
            for (let i = 0; i < M; i++) {
                let R_c_i = R_c * pow(k, i);
                for (let j = 0; j < N; j++) {
                    let angle = j * 2 * PI / N;
                    let x = R_c_i * cos(angle);
                    let y = R_c_i * sin(angle);
                    fill(140, 145, 157); // #8c929d
                    noStroke();
                    ellipse(x, y, 20);
                    stroke(0);
                    strokeWeight(1);
                    ellipse(x, y, 10);
                }
            }
            stroke(160, 160, 160); // #a0a0a0
            for (let j = 0; j < N; j++) {
                let angle = j * 2 * PI / N;
                let x = R_c * cos(angle);
                let y = R_c * sin(angle);
                line(0, 0, x, y);
            }
        }

        function drawInverseMode() {
            fill(255, 0, 0); // #ff0000
            noStroke();
            ellipse(locators[0].x, locators[0].y, 8);
            ellipse(locators[1].x, locators[1].y, 8);
            stroke(255, 0, 0); // #ff0000
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);
            let C_inv = p5.Vector.add(locators[0], locators[1]).div(2);
            let R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;
            fill(255, 0, 0, 0.3); // rgba(255, 0, 0, 0.3)
            stroke(0);
            ellipse(C_inv.x, C_inv.y, R_inv);
            for (let i = 0; i < locators.length; i++) {
                let C_orig = locators[i];
                let r_orig = 10;
                let d = p5.Vector.dist(C_inv, C_orig);
                if (d < r_orig) {
                    // Draw a line
                    let normal = p5.Vector.sub(C_orig, C_inv).normalize();
                    let intersection = p5.Vector.add(C_inv, normal.mult(R_inv^2 / (2 * d)));
                    line(C_inv.x, C_inv.y, intersection.x, intersection.y);
                } else {
                    // Draw a circle
                    let C_new = p5.Vector.add(C_inv, (R_inv^2 / (d^2 - r_orig^2)) * (C_orig - C_inv));
                    let r_new = abs(R_inv^2 / (d^2 - r_orig^2)) * r_orig;
                    fill(220, 100, 220, 0.5); // rgba(220, 100, 220, 0.5)
                    noStroke();
                    ellipse(C_new.x, C_new.y, r_new * 2);
                    stroke(0);
                }
            }
        }

        function mousePressed() {
            if (document.getElementById("checkbox-inverse").checked) {
                let x = mouseX - width / 2;
                let y = mouseY - height / 2;
                let d = p5.Vector.dist(locators[0], new p5.Vector(x, y));
                if (d < 4) {
                    draggedLocator = 0;
                } else {
                    d = p5.Vector.dist(locators[1], new p5.Vector(x, y));
                    if (d < 4) {
                        draggedLocator = 1;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== null) {
                let x = mouseX - width / 2;
                let y = mouseY - height / 2;
                locators[draggedLocator].x = x;
                locators[draggedLocator].y = y;
                redraw();
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }

        function changeRingValue() {
            redraw();
        }

        function changeCirclesPerRingValue() {
            redraw();
        }

        document.getElementById("rings-radios").addEventListener("change", changeRingValue);
        document.getElementById("cpr-radios").addEventListener("change", changeCirclesPerRingValue);
    </script>
</body>
</html>
