            background-color: #f0f0f0;
            border-bottom: 1px solid #ccc;
        }

        .slider-group {
            margin-right: 1rem;
        }

        label {
            margin-right: 0.5rem;
        }

        /* Visualization Area Styles */
        .visualization-area {
            position: relative;
            padding: 2rem;
        }

        /* Formula Display Styles */
        .formula {
            position: absolute;
            font-size: 1.2rem;
        }

        #formula-strong {
            top: 10%;
            left: 10%;
            color: maroon;
        }

        #formula-weak {
            top: 10%;
            right: 10%;
            color: green;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-group">
                <label>C_a</label>
                <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
                <span id="logCa-label">\(C_a\)</span>
            </div>
            <div class="slider-group">
                <label>pK_a</label>
                <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
                <span id="pka-label">\(pK_a\)</span>
            </div>
        </div>
        <div class="visualization-area">
            <canvas id="plot" width="800" height="600"></canvas>
            <div class="formula" id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
            <div class="formula" id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/dist/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.2/es5/tex-mml-chtml.js"></script>

    <script>
        let logCa = -1;
        let pKa = 1.2;

        function setup() {
            createCanvas(800, 600);
            background(220);
            noLoop();
        }

        function draw() {
            background(220);
            drawAxes();
            drawStrongAcidLine();
            drawWeakAcidLine();
            drawExactPHLine();
            drawIndicatorPoint();
        }

        function drawAxes() {
            stroke(50);
            line(50, 50, 750, 50); // x-axis
            line(50, 50, 50, 550); // y-axis

            // draw ticks and labels
            for (let i = -4; i <= 7; i++) {
                let x = map(i, -4, 7, 50, 750);
                let y = 50;
                line(x, y - 5, x, y + 5);
                if (i == -4 || i == 7) {
                    fill(0);
                    noStroke();
                    text(i, x - 10, y + 15);
                }
            }

            for (let i = 0; i <= 7; i++) {
                let x = 50;
                let y = map(i, 0, 7, 50, 550);
                line(x - 5, y, x + 5, y);
                if (i == 0 || i == 7) {
                    fill(0);
                    noStroke();
                    text(i, x - 15, y - 10);
                }
            }
        }

        function drawStrongAcidLine() {
            stroke(maroon);
            strokeWeight(2);
            let pH = -logCa;
            let y = map(pH, 0, 7, 550, 50);
            line(50, y, 750, y);
        }

        function drawWeakAcidLine() {
            stroke(green);
            strokeWeight(2);
            let pH = 0.5 * (pKa - logCa);
            let y = map(pH, 0, 7, 550, 50);
            let x = map(pKa, -4, 7, 50, 750);
            line(x, y, x + 10, y + 10);
        }

        function drawExactPHLine() {
            stroke(blue);
            strokeWeight(4);
            let ca = pow(10, logCa);
            for (let pka = -4; pka <= 7; pka++) {
                let pH = calculatePH(pka, ca);
                let y = map(pH, 0, 7, 550, 50);
                if (pka == pKa) {
                    fill(orange);
                    noStroke();
                    ellipse(50, y, 10, 10);
                }
                line(50, y, 50 + map(pka, -4, 7, 0, 700), y);
            }
        }

        function drawIndicatorPoint() {
            fill(orange);
            noStroke();
            ellipse(50 + map(pKa, -4, 7, 0, 700), map(calculatePH(pKa, pow(10, logCa)), 0, 7, 550, 50), 10, 10);
        }

        function calculatePH(pka, ca) {
            let ka = pow(10, -pka);
            let hPlus = (-ka + sqrt(ka ** 2 + ka * ca)) / 2;
            return -Math.log(hPlus);
        }

        function updatePlot() {
            logCa = document.getElementById("slider-logCa").valueAsNumber;
            pKa = document.getElementById("slider-pka").valueAsNumber;
            draw();
        }

        document.getElementById("slider-logCa").addEventListener("input", updatePlot);
        document.getElementById("slider-pka").addEventListener("input", updatePlot);

        setup();
        draw();
    </script>
</body>
</html>
