    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let grid = [];
        let start = { x: 0.15, y: 0.85 };
        let end = { x: 0.85, y: 0.15 };
        let obstacles = [];
        let history = [];
        let currentStep = 1;

        function setup() {
            createCanvas(800, 600);
            background(255);
            frameRate(30);

            // Initialize grid
            for (let i = 0; i < 50; i++) {
                grid[i] = [];
                for (let j = 0; j < 50; j++) {
                    grid[i][j] = { x: j, y: i, type: 'empty' };
                }
            }

            // Initialize start and end points
            start.x = map(start.x, 0, 1, 0, 49);
            start.y = map(start.y, 0, 1, 0, 49);
            end.x = map(end.x, 0, 1, 0, 49);
            end.y = map(end.y, 0, 1, 0, 49);
            grid[start.y][start.x].type = 'start';
            grid[end.y][end.x].type = 'end';

            // Initialize obstacles
            obstacles = [
                { x: 10, y: 10, w: 5, h: 5 },
                { x: 20, y: 20, w: 5, h: 5 },
                { x: 30, y: 30, w: 5, h: 5 },
                { x: 40, y: 40, w: 5, h: 5 },
            ];

            for (let obstacle of obstacles) {
                for (let i = obstacle.y; i < obstacle.y + obstacle.h; i++) {
                    for (let j = obstacle.x; j < obstacle.x + obstacle.w; j++) {
                        grid[i][j].type = 'obstacle';
                    }
                }
            }

            // Run pathfinding algorithm
            let algorithm = 'dijkstra';
            if (document.getElementById('radio-a-star').checked) {
                algorithm = 'a-star';
            }
            runAlgorithm(algorithm);
        }

        function draw() {
            background(255);
            drawGrid();
            drawStartAndEndPoints();
            drawObstacles();
            drawPath();
            drawHistory();
        }

        function drawGrid() {
            for (let i = 0; i < 50; i++) {
                for (let j = 0; j < 50; j++) {
                    let cell = grid[i][j];
                    if (cell.type === 'empty') {
                        fill(255);
                    } else if (cell.type === 'obstacle') {
                        fill(0);
                    } else if (cell.type === 'start') {
                        fill(0, 255, 0);
                    } else if (cell.type === 'end') {
                        fill(0, 255, 0);
                    } else if (cell.type === 'open') {
                        fill(173, 224, 254);
                    } else if (cell.type === 'closed') {
                        fill(0, 0, 205);
                    }
                    noStroke();
                    rect(j * 16, i * 16, 16, 16);
                }
            }
        }

        function drawStartAndEndPoints() {
            let startCell = grid[start.y][start.x];
            let endCell = grid[end.y][end.x];
            fill(0, 255, 0);
            noStroke();
            rect(startCell.x * 16, startCell.y * 16, 16, 16);
            rect(endCell.x * 16, endCell.y * 16, 16, 16);
        }

        function drawObstacles() {
            for (let obstacle of obstacles) {
                for (let i = obstacle.y; i < obstacle.y + obstacle.h; i++) {
                    for (let j = obstacle.x; j < obstacle.x + obstacle.w; j++) {
                        let cell = grid[i][j];
                        fill(0);
                        noStroke();
                        rect(j * 16, i * 16, 16, 16);
                    }
                }
            }
        }

        function drawPath() {
            if (currentStep === 176) {
                let path = history[currentStep - 1].path;
                stroke(255, 0, 0);
                strokeWeight(3);
                for (let i = 0; i < path.length - 1; i++) {
                    let cell1 = grid[path[i].y][path[i].x];
                    let cell2 = grid[path[i + 1].y][path[i + 1].x];
                    line(cell1.x * 16 + 8, cell1.y * 16 + 8, cell2.x * 16 + 8, cell2.y * 16 + 8);
                }
            }
        }

        function drawHistory() {
            let step = document.getElementById('slider-step').value;
            let historyFrame = history[step - 1];
            for (let i = 0; i < 50; i++) {
                for (let j = 0; j < 50; j++) {
                    let cell = grid[i][j];
                    if (cell.type === 'open' || cell.type === 'closed') {
                        let index = historyFrame.open.indexOf(`${i},${j}`);
                        if (index !== -1) {
                            fill(173, 224, 254);
                        } else {
                            fill(0, 0, 205);
                        }
                    }
                    noStroke();
                    rect(j * 16, i * 16, 16, 16);
                }
            }
        }

        function runAlgorithm(algorithm) {
            history = [];
            let open = [];
            let closed = [];
            let current = { x: start.x, y: start.y, cost: 0, parent: null };
            open.push(current);
            let heuristic = (x, y) => abs(x - end.x) + abs(y - end.y);
            while (open.length > 0) {
                let currentCell = open.sort((a, b) => a.cost + heuristic(a.x, a.y) - (b.cost + heuristic(b.x, b.y)))[0];
                open.splice(open.indexOf(currentCell), 1);
                closed.push(currentCell);
                if (currentCell.x === end.x && currentCell.y === end.y) {
                    break;
                }
                let neighbors = getNeighbors(currentCell);
                for (let neighbor of neighbors) {
                    let newCost = currentCell.cost + 1;
                    let index = open.findIndex((cell) => cell.x === neighbor.x && cell.y === neighbor.y);
                    if (index !== -1) {
                        if (newCost < open[index].cost) {
                            open[index].cost = newCost;
                            open[index].parent = currentCell;
                        }
                    } else {
                        open.push({ x: neighbor.x, y: neighbor.y, cost: newCost, parent: currentCell });
                    }
                }
                history.push({ open, closed, path: getShortestPath(currentCell) });
            }
            currentStep = 1;
            document.getElementById('slider-step').max = history.length;
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
            history.forEach((frame, index) => {
                let path = frame.path;
                for (let i = 0; i < path.length; i++) {
                    let cell = grid[path[i].y][path[i].x];
                    cell.type = 'open';
                }
            });
        }

        function getNeighbors(cell) {
            let neighbors = [];
            for (let i = -1; i <= 1; i++) {
                for (let j = -1; j <= 1; j++) {
                    if (i === 0 && j === 0) {
                        continue;
                    }
                    let x = cell.x + i;
                    let y = cell.y + j;
                    if (x >= 0 && x < 50 && y >= 0 && y < 50 && grid[y][x].type !== 'obstacle') {
                        neighbors.push({ x, y });
                    }
                }
            }
            return neighbors;
        }

        function getShortestPath(cell) {
            let path = [];
            while (cell) {
                path.push({ x: cell.x, y: cell.y });
                cell = cell.parent;
            }
            return path.reverse();
        }

        // p5.js events
        function mousePressed() {
            let joystick = document.getElementById('joystick-start');
            if (joystick.contains(event.clientX, event.clientY)) {
                let handle = document.getElementById('handle-start');
                handle.style.top = event.clientY - handle.offsetHeight / 2 + 'px';
                handle.style.left = event.clientX - handle.offsetWidth / 2 + 'px';
                start.x = map(event.clientX, 0, 800, 0, 49);
                start.y = map(event.clientY, 0, 600, 0, 49);
                runAlgorithm('dijkstra');
            }
        }

        function mouseDragged() {
            let joystick = document.getElementById('joystick-start');
            if (joystick.contains(event.clientX, event.clientY)) {
                let handle = document.getElementById('handle-start');
                handle.style.top = event.clientY - handle.offsetHeight / 2 + 'px';
                handle.style.left = event.clientX - handle.offsetWidth / 2 + 'px';
                start.x = map(event.clientX, 0, 800, 0, 49);
                start.y = map(event.clientY, 0, 600, 0, 49);
            }
        }

        function mouseReleased() {
            let joystick = document.getElementById('joystick-start');
            if (joystick.contains(event.clientX, event.clientY)) {
                let handle = document.getElementById('handle-start');
                handle.style.top = event.clientY - handle.offsetHeight / 2 + 'px';
                handle.style.left = event.clientX - handle.offsetWidth / 2 + 'px';
                start.x = map(event.clientX, 0, 800, 0, 49);
                start.y = map(event.clientY, 0, 600, 0, 49);
                runAlgorithm('dijkstra');
            }
        }

        function changeObstacles() {
            let value = document.getElementById('select-obstacles').value;
            obstacles = [];
            if (value === '1-square') {
                obstacles.push({ x: 10, y: 10, w: 5, h: 5 });
            } else if (value === '2-squares') {
                obstacles.push({ x: 10, y: 10, w: 5, h: 5 });
                obstacles.push({ x: 20, y: 20, w: 5, h: 5 });
            } else if (value === 'l-shaped-wall') {
                obstacles.push({ x: 10, y: 10, w: 5, h: 5 });
                obstacles.push({ x: 15, y: 15, w: 5, h: 5 });
                obstacles.push({ x: 20, y: 20, w: 5, h: 5 });
            }
            for (let obstacle of obstacles) {
                for (let i = obstacle.y; i < obstacle.y + obstacle.h; i++) {
                    for (let j = obstacle.x; j < obstacle.x + obstacle.w; j++) {
                        grid[i][j].type = 'obstacle';
                    }
                }
            }
            runAlgorithm('dijkstra');
        }

        function changeAlgorithm() {
            let algorithm = 'dijkstra';
            if (document.getElementById('radio-a-star').checked) {
                algorithm = 'a-star';
            }
            runAlgorithm(algorithm);
        }

        function changeStep() {
            let step = document.getElementById('slider-step').value;
            currentStep = step;
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
            let historyFrame = history[step - 1];
            for (let i = 0; i < 50; i++) {
                for (let j = 0; j < 50; j++) {
                    let cell = grid[i][j];
                    if (cell.type === 'open' || cell.type === 'closed') {
                        let index = historyFrame.open.indexOf(`${i},${j}`);
                        if (index !== -1) {
                            fill(173, 224, 254);
                        } else {
                            fill(0, 0, 205);
                        }
                    }
                    noStroke();
                    rect(j * 16, i * 16, 16, 16);
                }
            }
        }

        // Add event listeners
        document.getElementById('joystick-start').addEventListener('mousedown', mousePressed);
        document.getElementById('joystick-start').addEventListener('mousemove', mouseDragged);
        document.getElementById('joystick-start').addEventListener('mouseup', mouseReleased);
        document.getElementById('select-obstacles').addEventListener('change', changeObstacles);
        document.getElementById('radio-dijkstra').addEventListener('change', changeAlgorithm);
        document.getElementById('radio-a-star').addEventListener('change', changeAlgorithm);
        document.getElementById('slider-step').addEventListener('input', changeStep);

        // Run p5.js sketch
        setup();
        draw();
    </script>
</body>
</html>
