            triangle(base.x + vector.x * 100, base.y + vector.y * 100,
                     base.x + vector.x * 100 - vector.y * 20, base.y + vector.y * 100 + vector.x * 20,
                     base.x + vector.x * 100 + vector.y * 20, base.y + vector.y * 100 - vector.x * 20);
        }

        function drawElectricField() {
            let r = dist(width / 2, height / 2, mouseX, mouseY);
            let r_hat = createVector((mouseX - width / 2) / r, (mouseY - height / 2) / r);
            let E_vec = p5.Vector.mult(r_hat, k * Q / (r * r));
            drawArrow(createVector(mouseX, mouseY), E_vec, color(255, 0, 0));
        }

        function drawElectricForce() {
            let F_vec = q * E_vec;
            if (q !== 0) {
                drawArrow(createVector(mouseX, mouseY), F_vec, color(0, 0, 255));
            }
        }

        function drawLabels() {
            fill(0);
            textSize(16);
            textAlign(LEFT, TOP);
            text('x (m)', 10, 20);
            text('y (m)', 450, 20);
        }

        function updateSourceCharge() {
            Q = sliderSourceCharge.value();
            updateInfoPanel();
            drawInitialScene();
        }

        function updateTestCharge() {
            q = sliderTestCharge.value();
            updateInfoPanel();
            drawInitialScene();
        }

        function updateInfoPanel() {
            let E_mag = k * Q / dist(width / 2, height / 2, mouseX, mouseY);
            let Ex = E_vec.x;
            let Ey = E_vec.y;
            let F = q * E_mag;
            document.getElementById('display-E-mag').innerText = `|E| = ${format(E_mag, 3)} N/C`;
            document.getElementById('display-Ex').innerText = `E<sub>x</sub> component = ${format(Ex, 3)} N/C`;
            document.getElementById('display-Ey').innerText = `E<sub>y</sub> component = ${format(Ey, 3)} N/C`;
            document.getElementById('display-x-pos').innerText = `x position = ${format(mouseX, 2)} m`;
            document.getElementById('display-y-pos').innerText = `y position = ${format(mouseY, 2)} m`;
            document.getElementById('display-force').innerText = `Electric force = ${format(F, 3)} N`;
        }

        function drawInitialScene() {
            E_vec = createVector(0, 0);
            draw();
        }

        function format(value, decimalPlaces) {
            return value.toFixed(decimalPlaces);
        }

        function mousePressed() {
            dragging = true;
        }

        function mouseDragged() {
            if (dragging) {
                x = mouseX;
                y = mouseY;
                draw();
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
