        // Define the functions
        function f_H(r) {
            if (r <= 1) return (1 - r**2)**2.5;
            else return 0;
        }

        function f_O(r) {
            if (r <= 1) return (1 - r)**2;
            else return 0;
        }

        function f_G(r) {
            return Math.exp(-Math.PI * r**2);
        }

        function F_H(k) {
            if (k == 0) return 0;
            return 15 * Math.PI * ((3 - k**2) * Math.sin(k) - 3 * k * Math.cos(k)) / (k**5);
        }

        function F_O(k) {
            if (k == 0) return 0;
            return 8 * Math.PI * (2 - k**2 - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / (k**4);
        }

        function F_G(k) {
            return (1/Math.sqrt(Math.PI)) * Math.exp(-k**2 / (4*Math.PI));
