    <script>
        let neutralYear = 2040;
        let scale = 'individual';
        let type = 'emissions';
        let population = 250;
        let annualEmissions = 14;
        let reductionTime = 20;
        let annualBudget = 1.5;
        let startYear = 2020;
        let compensationCost = 15;
        let fractionalCompensation = 50;
        let constantCompensation = 25;

        function setup() {
            createCanvas(800, 600);
            background(220);
            frameRate(30);
            noLoop();
        }

        function draw() {
            background(220);
            drawMainPlot();
            drawBudgetPlot();
        }

        function drawMainPlot() {
            if (type === 'emissions') {
                drawEmissionsPlot();
            } else if (type === 'costs') {
                drawCostsPlot();
            }
        }

        function drawEmissionsPlot() {
            // Plot real emissions (red line)
            stroke(255, 0, 0);
            strokeWeight(2);
            beginShape();
            for (let t = 2020; t <= 2050; t++) {
                let x = map(t, 2020, 2050, 0, width);
                let y = map(getRealEmissions(t), 0, 25, height, 0);
                vertex(x, y);
            }
            endShape();

            // Plot effective emissions (black line)
            stroke(0);
            strokeWeight(2);
            beginShape();
            for (let t = 2020; t <= 2050; t++) {
                let x = map(t, 2020, 2050, 0, width);
                let y = map(getEffectiveEmissions(t), 0, 25, height, 0);
                vertex(x, y);
            }
            endShape();

            // Fill area between red and black lines with semi-transparent blue
            noStroke();
            fill(0, 0, 255, 50);
            beginShape();
            for (let t = 2020; t <= 2050; t++) {
                let x = map(t, 2020, 2050, 0, width);
                let y1 = map(getRealEmissions(t), 0, 25, height, 0);
                let y2 = map(getEffectiveEmissions(t), 0, 25, height, 0);
                vertex(x, y1);
                vertex(x, y2);
            }
            endShape(CLOSE);

            // Fill area between black line and y=0 axis with semi-transparent red where effective emissions > 0
            if (getEffectiveEmissions(2020) > 0) {
                fill(255, 0, 0, 50);
                beginShape();
                for (let t = 2020; t <= 2050; t++) {
                    let x = map(t, 2020, 2050, 0, width);
                    let y = map(getEffectiveEmissions(t), 0, 25, height, 0);
                    if (y > 0) {
                        vertex(x, y);
                    } else {
                        vertex(x, 0);
                    }
                }
                endShape(CLOSE);
            }

            // Fill area between black line and y=0 axis with semi-transparent green where effective emissions < 0
            if (getEffectiveEmissions(2020) < 0) {
                fill(0, 255, 0, 50);
                beginShape();
                for (let t = 2020; t <= 2050; t++) {
                    let x = map(t, 2020, 2050, 0, width);
                    let y = map(getEffectiveEmissions(t), 0, 25, height, 0);
                    if (y < 0) {
                        vertex(x, y);
                    } else {
                        vertex(x, 0);
                    }
                }
                endShape(CLOSE);
            }

            // Draw vertical line at neutral year
            stroke(0);
            strokeWeight(2);
            line(map(neutralYear, 2020, 2050, 0, width), 0, map(neutralYear, 2020, 2050, 0, width), height);

            // Draw horizontal dashed line at cumulative effective emissions at neutral year
            stroke(0);
            strokeWeight(1);
            line(0, map(getCumulativeEffective(neutralYear), 0, 25, height, 0), width, map(getCumulativeEffective(neutralYear), 0, 25, height, 0));

            // Draw horizontal dashed line at cumulative effective emissions at 2050
            stroke(0);
            strokeWeight(1);
            line(0, map(getCumulativeEffective(2050), 0, 25, height, 0), width, map(getCumulativeEffective(2050), 0, 25, height, 0));
        }

        function drawCostsPlot() {
            // Plot annual costs (blue line)
            stroke(0, 0, 255);
            strokeWeight(2);
            beginShape();
            for (let t = 2020; t <= 2050; t++) {
                let x = map(t, 2020, 2050, 0, width);
                let y = map(getAnnualCosts(t), 0, 1000, height, 0);
                vertex(x, y);
            }
            endShape();

            // Fill area under blue line with semi-transparent blue
            noStroke();
            fill(0, 0, 255, 50);
            beginShape();
            for (let t = 2020; t <= 2050; t++) {
                let x = map(t, 2020, 2050, 0, width);
                let y = map(getAnnualCosts(t), 0, 1000, height, 0);
                vertex(x, y);
            }
            endShape(CLOSE);

            // Draw horizontal dashed line at average annual cost
            stroke(0);
            strokeWeight(1);
            line(0, map(getTotalCosts() / 31, 0, 1000, height, 0), width, map(getTotalCosts() / 31, 0, 1000, height, 0));

            // Draw text label for total personal compensation costs
            fill(0);
            textSize(16);
            text(`Total personal compensation costs (until 2050) = $${getTotalCosts()}`, 20, 40);
        }

        function drawBudgetPlot() {
            for (let t = 2020; t <= 2050; t += 10) {
                let x = map(t, 2020, 2050, 0, width);
                let y = map(getBudget(t), 0, 25, height, 0);
                let style = getStyle(t);
                let size = getSize(t);
                drawCircle(x, y, size, style);
            }
        }

        function drawCircle(x, y, size, style) {
            fill(style);
            noStroke();
            ellipse(x, y, size * 2);
            fill(0);
            textSize(16);
            text(`${round(getBudget(t))} tons CO₂`, x - 50, y + 10);
        }

        function getRealEmissions(t) {
            if (t < startYear) {
                return annualEmissions;
            } else {
                return annualEmissions * Math.pow(0.5, (t - startYear) / reductionTime);
            }
        }

        function getEffectiveEmissions(t) {
            let realEmissions = getRealEmissions(t);
            let compensatedEmissions = getCompensatedEmissions(t);
            return realEmissions - compensatedEmissions;
        }

        function getCompensatedEmissions(t) {
            let realEmissions = getRealEmissions(t);
            let fractional = realEmissions * (fractionalCompensation / 100);
            let constant = annualEmissions * (constantCompensation / 100);
            return fractional + constant;
        }

        function getCumulativeEffective(t) {
            let cumulative = 0;
            for (let i = 2020; i <= t; i++) {
                cumulative += getEffectiveEmissions(i);
            }
            return cumulative;
        }

        function getAnnualCosts(t) {
            let compensatedEmissions = getCompensatedEmissions(t);
            return compensatedEmissions * compensationCost;
        }

        function getTotalCosts() {
            let total = 0;
            for (let i = 2020; i <= 2050; i++) {
                total += getAnnualCosts(i);
            }
            return total;
        }

        function getBudget(t) {
            let totalBudget = 30 * annualBudget;
            return totalBudget - getCumulativeEffective(t);
        }

        function getStyle(t) {
            if (getEffectiveEmissions(t) <= 0) {
                return color(0, 255, 0, 255);
            } else {
                return color(128, 128, 128, 255);
            }
        }

        function getSize(t) {
            let value = getBudget(t);
            let size = sqrt(Math.abs(value)) * 10;
            return size;
        }

        function update() {
            draw();
        }

        setup();

        // Event listeners
        document.getElementById('scale-switch').addEventListener('change', function() {
            scale = document.querySelector('input[name="scale"]:checked').value;
            update();
        });

        document.getElementById('type-switch').addEventListener('change', function() {
            type = document.querySelector('input[name="type"]:checked').value;
            update();
        });

        document.getElementById('slider-population').addEventListener('input', function() {
            population = parseInt(this.value);
            update();
        });

        document.getElementById('slider-annual-emissions').addEventListener('input', function() {
            annualEmissions = parseFloat(this.value);
            update();
        });

        document.getElementById('slider-reduction-time').addEventListener('input', function() {
            reductionTime = parseInt(this.value);
            update();
        });

        document.getElementById('slider-annual-budget').addEventListener('input', function() {
            annualBudget = parseFloat(this.value);
            update();
        });

        document.getElementById('slider-start-year').addEventListener('input', function() {
            startYear = parseInt(this.value);
            update();
        });

        document.getElementById('slider-compensation-cost').addEventListener('input', function() {
            compensationCost = parseInt(this.value);
            update();
        });

        document.getElementById('slider-fractional-compensation').addEventListener('input', function() {
            fractionalCompensation = parseInt(this.value);
            update();
        });

        document.getElementById('slider-constant-compensation').addEventListener('input', function() {
            constantCompensation = parseInt(this.value);
            update();
        });

        document.getElementById('btn-neutral-now').addEventListener('click', function() {
            neutralYear = 2020;
            update();
        });

        document.getElementById('btn-neutral-2030').addEventListener('click', function() {
            neutralYear = 2030;
            update();
        });

        document.getElementById('btn-neutral-2040').addEventListener('click', function() {
            neutralYear = 2040;
            update();
        });

        document.getElementById('btn-neutral-2050').addEventListener('click', function() {
            neutralYear = 2050;
            update();
        });

        document.getElementById('btn-neutral-never').addEventListener('click', function() {
            neutralYear = 2050;
            update();
        });
    </script>
</body>
</html>
