    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Initialize the scene, camera, and renderer
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);

        const renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('canvas-container'),
            antialias: true
        });

        // Initialize the controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Load the polyhedra geometries
        const geometries = {
            solid1: {
                vertices: [
                    new THREE.Vector3(-1, -1, 0),
                    new THREE.Vector3(1, -1, 0),
                    new THREE.Vector3(1, 1, 0),
                    new THREE.Vector3(-1, 1, 0)
                ],
                faces: [
                    [0, 1, 2],
                    [2, 3, 0],
                    [0, 2, 1],
                    [1, 3, 2],
                    [0, 3, 1],
                    [2, 0, 3]
                ],
                hinges: [
                    [0, 1],
                    [1, 2],
                    [2, 3],
                    [3, 0]
                ],
                angles: [
                    Math.PI / 2,
                    Math.PI / 2,
                    Math.PI / 2,
                    Math.PI / 2
                ]
            },
            solid2: {
                // Define the geometry for Solid 2
            },
            solid3: {
                // Define the geometry for Solid 3
            }
        };

        // Function to create a new polyhedron
        function createPolyhedron(geometry) {
            const group = new THREE.Group();
            const mesh = new THREE.Mesh(geometry, new THREE.MeshStandardMaterial({ side: THREE.DoubleSide, flatShading: true }));
            group.add(mesh);
            return group;
        }

        // Function to update the folding state
        function updateFoldingState(group, t) {
            const hinges = geometries.solid1.hinges;
            for (let i = 0; i < hinges.length; i++) {
                const hinge = hinges[i];
                const angle = geometries.solid1.angles[i] * t;
                const pivot = new THREE.Object3D();
                pivot.position.copy(group.position);
                pivot.rotation.copy(group.rotation);
                pivot.rotation.y = angle;
                group.add(pivot);
                const face = group.children.find(child => child instanceof THREE.Mesh && child.geometry.index === i);
                face.position.copy(pivot.position);
                face.rotation.copy(pivot.rotation);
            }
        }

        // Function to update the colors
        function updateColors(group, t) {
            const colors = [
                0xf5e0e0, // light pink
                0xe47a55, // orange
                0xa4c6e3, // light blue
                0xf4e0e1, // pink
                0xf3c76e // yellow
            ];
            for (let i = 0; i < group.children.length; i++) {
                const child = group.children[i];
                if (child instanceof THREE.Mesh) {
                    const color = colors[i % colors.length];
                    child.material.color.setHex(color);
                }
            }
        }

        // Function to reset the view
        function resetView() {
            controls.reset();
        }

        // Add event listeners
        document.getElementById('btn-solid-1').addEventListener('click', () => {
            const group = createPolyhedron(geometries.solid1);
            scene.add(group);
            updateFoldingState(group, 0);
            updateColors(group, 0);
        });

        document.getElementById('btn-solid-2').addEventListener('click', () => {
            const group = createPolyhedron(geometries.solid2);
            scene.add(group);
            updateFoldingState(group, 0);
            updateColors(group, 0);
        });

        document.getElementById('btn-solid-3').addEventListener('click', () => {
            const group = createPolyhedron(geometries.solid3);
            scene.add(group);
            updateFoldingState(group, 0);
            updateColors(group, 0);
        });

        document.getElementById('slider-fold').addEventListener('input', () => {
            const t = parseFloat(document.getElementById('slider-fold').value);
            const group = scene.children.find(child => child instanceof THREE.Group);
            updateFoldingState(group, t);
            updateColors(group, t);
        });

        document.getElementById('btn-reset-view').addEventListener('click', resetView);

        // Animate the scene
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
