            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" value="0.6" min="0.1" max="5.0" step="0.01">
                <span class="value-display" id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" value="3.93" min="0.1" max="5.0" step="0.01">
                <span class="value-display" id="value-bc">3.93</span>
            </div>
