    <script>
        let alpha13 = 0.6;
        let alpha23 = 3.93;

        function setup() {
            createCanvas(600, 550);
            background(220);
            stroke(0);
            strokeWeight(1);
            noLoop();
        }

        function draw() {
            background(220);
            stroke(0);
            strokeWeight(1);
            fill(0);
            drawTriangle(0, 0, 0, 550, 0, 550);
            drawGrid(10);
            drawLabels();
        }

        function drawTriangle(x1, y1, x2, y2, x3, y3) {
            beginShape();
            vertex(x1, y1);
            vertex(x2, y2);
            vertex(x3, y3);
            endShape(CLOSE);
        }

        function drawGrid(n) {
            let w = width / n;
            let h = height / n;
            for (let i = 0; i < n; i++) {
                for (let j = 0; j < n; j++) {
                    let x = i * w;
                    let y = j * h;
                    let centroidX = x + w / 2;
                    let centroidY = y + h / 2;
                    let xA = alpha13 * centroidX;
                    let xB = alpha23 * centroidX;
                    let xC = (1 - alpha13 - alpha23) * centroidX;
                    let yA = alpha13 * centroidY;
                    let yB = alpha23 * centroidY;
                    let yC = (1 - alpha13 - alpha23) * centroidY;
                    let f = (xA - xA) ** 2 + (xB - xB) ** 2 + (xC - xC) ** 2;
                    let v = f / 2.0;
                    let r = map(v, 0, 1, 0, 255);
                    let g = map(v, 0, 1, 0, 255);
                    let b = map(v, 0, 1, 0, 255);
                    fill(r, g, b);
                    drawTriangle(x, y, x + w, y, x + w / 2, y + h);
                }
            }
        }

        function drawLabels() {
            fill(0);
            textSize(18);
            textAlign(CENTER, CENTER);
            text('A', 50, 50);
            text('B', 250, 50);
            text('C', 450, 50);
            for (let i = 0; i <= 10; i++) {
                let x = map(i, 0, 10, 0, width);
                let y = height - 20;
                text(i / 10, x, y);
            }
            for (let i = 0; i <= 10; i++) {
                let x = 20;
                let y = map(i, 0, 10, 0, height);
                text(i / 10, x, y);
            }
        }

        function updatePlot() {
            alpha13 = parseFloat(document.getElementById('slider-ac').value);
            alpha23 = parseFloat(document.getElementById('slider-bc').value);
            background(220);
            draw();
        }

        function resetPlot() {
            document.getElementById('slider-ac').value = 0.6;
            document.getElementById('slider-bc').value = 3.93;
            updatePlot();
        }

        document.getElementById('slider-ac').addEventListener('input', updatePlot);
        document.getElementById('slider-bc').addEventListener('input', updatePlot);
        document.getElementById('btn-reset').addEventListener('click', resetPlot);

        setup();
        draw();
    </script>
</body>
</html>
